import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BestFromAuthorSection = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [error, setError] = useState(null);
  const [bestAuthor, setBestAuthor] = useState(null);
  const [authorStats, setAuthorStats] = useState(null);

  useEffect(() => {
    fetchRecommendations();
  }, []);

  const fetchRecommendations = async (forceRefresh = false) => {
    try {
      const isRefreshing = forceRefresh;
      if (isRefreshing) {
        setRefreshing(true);
      } else {
        setLoading(true);
      }
      
      setError(null);
      
      const token = localStorage.getItem('token');
      
      // Add cache busting for force refresh
      const cacheBuster = forceRefresh ? `?t=${Date.now()}` : '';
      const endpoint = forceRefresh 
        ? 'http://localhost:5000/api/recommendations/best-from-author/refresh'
        : 'http://localhost:5000/api/recommendations/best-from-author';
      
      const method = forceRefresh ? 'POST' : 'GET';
      const fullUrl = endpoint + cacheBuster;
      
      console.log(`ðŸ”„ Best From Author: ${forceRefresh ? 'Fetching FRESH data with cache bust' : 'Using cached data'}`);
      
      const response = await axios({
        method: method,
        url: fullUrl,
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
          'Cache-Control': forceRefresh ? 'no-cache, no-store, must-revalidate' : 'max-age=300'
        }
      });

      if (response.data && response.data.recommendations) {
        setRecommendations(response.data.recommendations);
        setBestAuthor(response.data.best_author);
        setAuthorStats({
          affinity_score: response.data.author_affinity_score,
          books_liked: response.data.books_liked_by_author,
          total_interactions: response.data.total_interactions_with_author
        });
      } else {
        setRecommendations([]);
        setBestAuthor(null);
        setAuthorStats(null);
      }
      
    } catch (error) {
      console.error('Error fetching best-from-author recommendations:', error);
      setError('Failed to load recommendations');
      setRecommendations([]);
      setBestAuthor(null);
      setAuthorStats(null);
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  };

  const handleRefresh = () => {
    fetchRecommendations(true); // Force refresh
  };

  const handleBookInteraction = async (book, action) => {
    try {
      const token = localStorage.getItem('token');
      const payload = {
        book_name: book.name,
        book_author: book.author || '',
        book_genres: book.genres || ''
      };

      let endpoint = '';
      if (action === 'like') {
        endpoint = 'http://localhost:5000/api/books/like';
      } else if (action === 'dislike') {
        endpoint = 'http://localhost:5000/api/books/dislike';
      }

      await axios.post(endpoint, payload, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      // Refresh recommendations after interaction to update user interaction status
      fetchRecommendations();

    } catch (error) {
      console.error('Interaction error:', error);
    }
  };

  const handleWishlistToggle = async (book) => {
    try {
      const token = localStorage.getItem('token');
      const payload = {
        book_name: book.name,
        book_author: book.author || '',
        book_genres: book.genres || ''
      };

      await axios.post('http://localhost:5000/api/wishlist/toggle', payload, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      // Refresh recommendations to update wishlist status
      fetchRecommendations();

    } catch (error) {
      console.error('Wishlist error:', error);
    }
  };

  if (loading) {
    return (
      <div className="glass-card rounded-2xl p-6 mb-8">
        <div className="flex items-center justify-center py-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-gray-400"></div>
          <span className="ml-3 text-gray-400">Finding your favorite author...</span>
        </div>
      </div>
    );
  }

  if (error || recommendations.length === 0) {
    return (
      <div className="glass-card rounded-2xl p-6 mb-8">
        <div className="flex items-center mb-4">
          <svg className="w-6 h-6 text-gray-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
          </svg>
          <h2 className="text-xl font-semibold text-white">Best From The Author</h2>
          {refreshing && (
            <span className="ml-2 text-sm text-gray-400 bg-gray-400/20 px-2 py-1 rounded-full flex items-center">
              <div className="animate-spin rounded-full h-3 w-3 border-b border-gray-400 mr-1"></div>
              Refreshing
            </span>
          )}
        </div>
        <div className="text-center py-8">
          <p className="text-gray-400 mb-2">
            {error || 'Like some books to discover your favorite authors!'}
          </p>
          <p className="text-gray-500 text-sm">
            We'll recommend unread books from the authors you love most.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="glass-card rounded-2xl p-6 mb-8">
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center">
          <svg className="w-6 h-6 text-gray-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
          </svg>
          <div className="flex flex-col">
            <h2 className="text-xl font-semibold text-white">Best From The Author</h2>
            {bestAuthor && (
              <div className="flex items-center mt-1">
                <span className="text-sm text-gray-400">More from </span>
                <span className="text-sm text-orange-400 font-medium ml-1">{bestAuthor}</span>
                {authorStats && (
                  <span className="text-xs text-gray-500 ml-2">
                    ({authorStats.books_liked} books liked)
                  </span>
                )}
              </div>
            )}
          </div>
          {recommendations.length > 0 && (
            <span className="ml-4 text-sm text-gray-400 bg-gray-400/20 px-2 py-1 rounded-full">
              {recommendations.length} books
            </span>
          )}
          {refreshing && (
            <span className="ml-2 text-sm text-gray-400 bg-gray-400/20 px-2 py-1 rounded-full flex items-center">
              <div className="animate-spin rounded-full h-3 w-3 border-b border-gray-400 mr-1"></div>
              Refreshing
            </span>
          )}
        </div>
        <div className="flex items-center space-x-2">
          <button
            onClick={handleRefresh}
            disabled={refreshing}
            className={`text-gray-400 hover:text-white transition-colors duration-200 ${
              refreshing ? 'opacity-50 cursor-not-allowed' : ''
            }`}
            title="Force refresh (recalculate recommendations)"
          >
            <svg
              className={`w-5 h-5 ${refreshing ? 'animate-spin' : ''}`}
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
            </svg>
          </button>
        </div>
      </div>

      <div className="flex overflow-x-auto space-x-4 pb-4 scrollbar-thin scrollbar-thumb-gray-600 scrollbar-track-gray-800">
        {recommendations
          .filter(book => !book.user_interaction) // Filter out books user has already interacted with
          .reduce((unique, book) => {
            // More aggressive deduplication: check if book name already exists
            const isDuplicate = unique.some(existingBook => 
              existingBook.name.toLowerCase().trim() === book.name.toLowerCase().trim()
            );
            if (!isDuplicate) {
              unique.push(book);
            }
            return unique;
          }, [])
          .slice(0, 10) // Limit to exactly 10 unique books
          .map((book, index) => (
          <div key={index} className="recommendation-card bg-white/5 backdrop-blur-sm border border-white/10 rounded-xl p-4 hover:bg-white/10 hover:border-orange-400/30 transition-all duration-300 group flex-none w-64 relative">
            {/* Wishlist Star Button - Top Right Corner */}
            <button
              onClick={() => handleWishlistToggle(book)}
              className={`absolute top-2 right-2 p-1 rounded-full transition-all duration-200 ${
                book.in_wishlist
                  ? 'bg-yellow-500/20 text-yellow-400 border border-yellow-500'
                  : 'bg-white/10 text-gray-400 border border-white/20 hover:bg-yellow-500/20 hover:text-yellow-400 hover:border-yellow-500'
              }`}
              title={book.in_wishlist ? 'Remove from wishlist' : 'Add to wishlist'}
            >
              <svg className="w-4 h-4" fill={book.in_wishlist ? "currentColor" : "none"} stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11.049 2.927c.3-.921 1.603-.921 1.902 0l1.519 4.674a1 1 0 00.95.69h4.915c.969 0 1.371 1.24.588 1.81l-3.976 2.888a1 1 0 00-.363 1.118l1.518 4.674c.3.922-.755 1.688-1.538 1.118l-3.976-2.888a1 1 0 00-1.176 0l-3.976 2.888c-.783.57-1.838-.197-1.538-1.118l1.518-4.674a1 1 0 00-.363-1.118l-3.976-2.888c-.784-.57-.38-1.81.588-1.81h4.914a1 1 0 00.951-.69l1.519-4.674z" />
              </svg>
            </button>

            <div className="mb-3">
              <h4 className="font-semibold text-white text-sm leading-snug mb-1 line-clamp-2 pr-8" title={book.name}>
                {book.name}
              </h4>
              <p className="text-xs text-gray-400 mb-2 line-clamp-1" title={book.author}>
                {book.author || 'Unknown Author'}
              </p>
              
              {/* Author affinity indicator */}
              {authorStats && (
                <div className="text-xs text-orange-400 mb-2 flex items-center">
                  <svg className="w-3 h-3 mr-1" fill="currentColor" viewBox="0 0 24 24">
                    <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z" />
                  </svg>
                  Favorite author
                </div>
              )}

              {/* User Interaction Status */}
              {book.user_interaction && (
                <div className="text-xs text-green-400 mb-2 flex items-center">
                  <svg className="w-3 h-3 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                  </svg>
                  You {book.user_interaction}d this
                </div>
              )}
            </div>

            {/* Action Buttons - Show on hover, hide if already interacted */}
            {!book.user_interaction && (
              <div className="opacity-0 group-hover:opacity-100 transition-opacity duration-200 flex space-x-2">
                <button
                  onClick={() => handleBookInteraction(book, 'like')}
                  className="flex-1 flex items-center justify-center px-2 py-1 rounded bg-green-500/20 border border-green-500/30 text-green-400 hover:bg-green-500/30 transition-all duration-200 text-xs"
                  title="Like this book"
                >
                  <svg className="w-3 h-3 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 10h4.764a2 2 0 011.789 2.894l-3.5 7A2 2 0 0115.263 21h-4.017c-.163 0-.326-.02-.485-.06L7 20m7-10V5a2 2 0 00-2-2h-.095c-.5 0-.905.405-.905.905 0 .714-.211 1.412-.608 2.006L7 11v9m7-10h-2M7 20H5a2 2 0 01-2-2v-6a2 2 0 012-2h2.5" />
                  </svg>
                  Like
                </button>
                <button
                  onClick={() => handleBookInteraction(book, 'dislike')}
                  className="flex-1 flex items-center justify-center px-2 py-1 rounded bg-red-500/20 border border-red-500/30 text-red-400 hover:bg-red-500/30 transition-all duration-200 text-xs"
                  title="Dislike this book"
                >
                  <svg className="w-3 h-3 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 14H5.236a2 2 0 01-1.789-2.894l3.5-7A2 2 0 018.736 3h4.018c.163 0 .326.02.485.06L17 4m-7 10v5a2 2 0 002 2h.095c.5 0 .905-.405.905-.905 0-.714.211-1.412.608-2.006L17 13V4m-7 10h2m5-10h2a2 2 0 012 2v6a2 2 0 01-2 2h-2.5" />
                  </svg>
                  Pass
                </button>
              </div>
            )}
          </div>
        ))}
      </div>

    </div>
  );
};

export default BestFromAuthorSection;
