import React, { useState, useRef, useEffect } from 'react';
import './PopularBooks.css';

const PopularBooks = () => {
  const [popularBooks, setPopularBooks] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [hasLoaded, setHasLoaded] = useState(false);
  const [error, setError] = useState('');
  const [limit, setLimit] = useState(50);
  
  // AbortController ref to cancel previous requests
  const abortControllerRef = useRef(null);

  const loadPopularBooks = async () => {
    // Cancel previous request if it exists
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
    }

    // Create new AbortController for this request
    abortControllerRef.current = new AbortController();
    
    setIsLoading(true);
    setError('');
    setHasLoaded(true);

    try {
      const token = localStorage.getItem('token');
      
      const response = await fetch(
        `http://localhost:5000/api/books/popular?limit=${limit}`,
        {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
          signal: abortControllerRef.current.signal
        }
      );

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setPopularBooks(data.books || []);
      
    } catch (error) {
      // Don't show error for aborted requests
      if (error.name !== 'AbortError') {
        console.error('Popular books error:', error);
        setError('Failed to load popular books. Please try again.');
        setPopularBooks([]);
      }
    } finally {
      setIsLoading(false);
      abortControllerRef.current = null;
    }
  };

  const handleLimitChange = (newLimit) => {
    setLimit(newLimit);
  };

  const handleLikeBook = async (book) => {
    try {
      const token = localStorage.getItem('token');
      
      const response = await fetch('http://localhost:5000/api/books/like', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          book_name: book.name,
          book_author: book.author,
          book_genres: book.genres
        })
      });

      if (response.ok) {
        // Update the book in popular books
        setPopularBooks(prevBooks =>
          prevBooks.map(b =>
            b.name === book.name 
              ? { ...b, user_interaction: 'like', likes: (b.likes || 0) + 1 }
              : b
          )
        );
      }
    } catch (error) {
      console.error('Error liking book:', error);
    }
  };

  const handleDislikeBook = async (book) => {
    try {
      const token = localStorage.getItem('token');
      
      const response = await fetch('http://localhost:5000/api/books/dislike', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          book_name: book.name,
          book_author: book.author,
          book_genres: book.genres
        })
      });

      if (response.ok) {
        // Update the book in popular books
        setPopularBooks(prevBooks =>
          prevBooks.map(b =>
            b.name === book.name 
              ? { ...b, user_interaction: 'dislike', dislikes: (b.dislikes || 0) + 1 }
              : b
          )
        );
      }
    } catch (error) {
      console.error('Error disliking book:', error);
    }
  };

  return (
    <div className="popular-books">
      <div className="popular-header">
        <h2>Popular Books</h2>
        
        <div className="controls-container">
          <div className="limit-selector">
            <label htmlFor="limit-select">Number of books:</label>
            <select 
              id="limit-select"
              value={limit} 
              onChange={(e) => handleLimitChange(Number(e.target.value))}
              disabled={isLoading}
            >
              <option value={20}>20 books</option>
              <option value={50}>50 books</option>
              <option value={100}>100 books</option>
              <option value={200}>200 books</option>
            </select>
          </div>
          
          <button
            onClick={loadPopularBooks}
            disabled={isLoading}
            className="load-button"
          >
            {isLoading ? 'Loading...' : 'Load Popular Books'}
          </button>
        </div>
        
        {error && <div className="error-message">{error}</div>}
        
        {isLoading && (
          <div className="loading-message">
            <div className="spinner"></div>
            Loading popular books...
          </div>
        )}
      </div>

      {hasLoaded && !isLoading && (
        <div className="popular-results">
          <h3>Popular Books ({popularBooks.length} books loaded)</h3>
          
          {popularBooks.length === 0 ? (
            <div className="no-results">
              <p>No popular books found</p>
              <p>Try again or check your connection</p>
            </div>
          ) : (
            <div className="books-grid">
              {popularBooks.map((book, index) => (
                <div key={index} className="book-card">
                  <div className="book-rank">#{index + 1}</div>
                  
                  <div className="book-info">
                    <h4 className="book-title">{book.name}</h4>
                    <p className="book-author">by {book.author}</p>
                    <p className="book-genres">{book.genres}</p>
                    
                    <div className="book-stats">
                      <div className="rating">
                        ⭐ {book.star_rating || 'N/A'} 
                        ({book.num_ratings || 0} ratings)
                      </div>
                      
                      <div className="popularity-score">
                        🔥 Popularity: {book.popularity_score || 'N/A'}
                      </div>
                      
                      <div className="interactions">
                        👍 {book.likes || 0} | 👎 {book.dislikes || 0}
                      </div>
                      
                      {book.average_rating && (
                        <div className="community-rating">
                          Community: ⭐ {book.average_rating} 
                          ({book.total_ratings} ratings)
                        </div>
                      )}
                      
                      {book.language_of_book && (
                        <div className="language">
                          🌐 Language: {book.language_of_book}
                        </div>
                      )}
                    </div>
                  </div>
                  
                  <div className="book-actions">
                    <button
                      onClick={() => handleLikeBook(book)}
                      className={`action-button like-button ${book.user_interaction === 'like' ? 'active' : ''}`}
                      disabled={book.user_interaction === 'like'}
                    >
                      👍 {book.user_interaction === 'like' ? 'Liked' : 'Like'}
                    </button>
                    
                    <button
                      onClick={() => handleDislikeBook(book)}
                      className={`action-button dislike-button ${book.user_interaction === 'dislike' ? 'active' : ''}`}
                      disabled={book.user_interaction === 'dislike'}
                    >
                      👎 {book.user_interaction === 'dislike' ? 'Disliked' : 'Dislike'}
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
      
      {!hasLoaded && (
        <div className="welcome-message">
          <p>Click "Load Popular Books" to see the most popular books ranked by ratings and popularity!</p>
        </div>
      )}
    </div>
  );
};

export default PopularBooks;
