import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TrendingSection = () => {
  const [trendingBooks, setTrendingBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [timeRange, setTimeRange] = useState('week');

  useEffect(() => {
    fetchTrendingBooks();
  }, [timeRange]);

  const fetchTrendingBooks = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      // Since we don't have a specific trending endpoint, we'll use popular books
      const response = await axios.get(`http://localhost:5000/api/books/popular?limit=5`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      setTrendingBooks(response.data.books || []);
    } catch (error) {
      console.error('Error fetching trending books:', error);
      setTrendingBooks([]);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="glass-card rounded-2xl p-6">
        <div className="flex items-center justify-center py-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-orange-400"></div>
          <span className="ml-3 text-gray-400">Loading trending...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="glass-card rounded-2xl p-6">
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center">
          <svg className="w-6 h-6 text-orange-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6" />
          </svg>
          <h3 className="text-xl font-semibold text-white">Trending Now</h3>
        </div>
        <select
          value={timeRange}
          onChange={(e) => setTimeRange(e.target.value)}
          className="bg-white/10 border border-white/20 rounded-lg px-3 py-1 text-white text-sm focus:outline-none focus:ring-2 focus:ring-orange-500"
        >
          <option value="day">Today</option>
          <option value="week">This Week</option>
          <option value="month">This Month</option>
        </select>
      </div>

      <div className="space-y-4">
        {trendingBooks.length > 0 ? (
          trendingBooks.map((book, index) => (
            <div key={index} className="flex items-center space-x-4 p-3 bg-white/5 rounded-lg hover:bg-white/10 transition-colors">
              <div className="flex-shrink-0">
                <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-bold ${
                  index === 0 ? 'bg-yellow-500 text-yellow-900' :
                  index === 1 ? 'bg-gray-400 text-gray-900' :
                  index === 2 ? 'bg-orange-600 text-orange-100' :
                  'bg-gray-600 text-gray-200'
                }`}>
                  {index + 1}
                </div>
              </div>
              <div className="flex-1 min-w-0">
                <h4 className="text-white font-medium text-sm line-clamp-1">{book.name}</h4>
                <p className="text-gray-400 text-xs line-clamp-1">{book.author || 'Unknown Author'}</p>
                {book.star_rating && (
                  <div className="flex items-center mt-1">
                    <svg className="w-3 h-3 text-yellow-400 mr-1" fill="currentColor" viewBox="0 0 20 20">
                      <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                    </svg>
                    <span className="text-yellow-400 text-xs">{book.star_rating.toFixed(1)}</span>
                  </div>
                )}
              </div>
              <div className="flex items-center text-orange-400">
                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6" />
                </svg>
              </div>
            </div>
          ))
        ) : (
          <div className="text-center py-8">
            <svg className="mx-auto h-12 w-12 text-gray-400 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6" />
            </svg>
            <p className="text-gray-400">No trending data available</p>
            <p className="text-gray-500 text-sm mt-2">Check back later for updates</p>
          </div>
        )}
      </div>

      {/* Trending Categories */}
      <div className="mt-6 pt-6 border-t border-gray-700">
        <h4 className="text-lg font-semibold text-white mb-4">Trending Categories</h4>
        <div className="flex flex-wrap gap-2">
          {['Science Fiction', 'Romance', 'Mystery', 'Fantasy', 'Biography'].map((genre) => (
            <span
              key={genre}
              className="px-3 py-1 bg-orange-500/20 text-orange-400 rounded-full text-sm hover:bg-orange-500/30 cursor-pointer transition-colors"
            >
              {genre}
            </span>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TrendingSection;
