import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RecommendedForYouSection = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isNewUser, setIsNewUser] = useState(false);
  const [newUserMessage, setNewUserMessage] = useState('');
  const [interactionCount, setInteractionCount] = useState(0);

  useEffect(() => {
    fetchRecommendations();
  }, []);

  const fetchRecommendations = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/books/recommendations/collaborative', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      
      const data = response.data;
      setRecommendations(data.recommendations || []);
      setIsNewUser(data.is_new_user || false);
      setNewUserMessage(data.message || '');
      setInteractionCount(data.interaction_count || 0);
      setError(null);
    } catch (error) {
      console.error('Error fetching collaborative recommendations:', error);
      setError('Failed to load recommendations');
    } finally {
      setLoading(false);
    }
  };

  const handleBookInteraction = async (book, action) => {
    try {
      const token = localStorage.getItem('token');
      const payload = {
        book_name: book.name,
        book_author: book.author || '',
        book_genres: book.genres || ''
      };

      let endpoint = '';
      if (action === 'like') {
        endpoint = 'http://localhost:5000/api/books/like';
      } else if (action === 'dislike') {
        endpoint = 'http://localhost:5000/api/books/dislike';
      }

      await axios.post(endpoint, payload, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      // Refresh recommendations after interaction
      fetchRecommendations();

    } catch (error) {
      console.error('Interaction error:', error);
    }
  };

  const handleWishlistToggle = async (book) => {
    try {
      const token = localStorage.getItem('token');
      const payload = {
        book_name: book.name,
        book_author: book.author || '',
        book_genres: book.genres || ''
      };

      await axios.post('http://localhost:5000/api/wishlist/toggle', payload, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      // Refresh recommendations to update wishlist status
      fetchRecommendations();

    } catch (error) {
      console.error('Wishlist error:', error);
    }
  };

  if (loading) {
    return (
      <div className="reading-section mb-12">
        <div className="flex items-center justify-center py-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-brand-primary"></div>
          <span className="ml-3 text-reading-text-secondary">Loading recommendations...</span>
        </div>
      </div>
    );
  }

  // Handle new user case
  if (isNewUser) {
    return (
      <div className="sidebar-section rounded-2xl mb-12">
        <div className="flex items-center mb-4">
          <svg className="w-6 h-6 text-brand-primary mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4M7.835 4.697a3.42 3.42 0 001.946-.806 3.42 3.42 0 014.438 0 3.42 3.42 0 001.946.806 3.42 3.42 0 013.138 3.138 3.42 3.42 0 00.806 1.946 3.42 3.42 0 010 4.438 3.42 3.42 0 00-.806 1.946 3.42 3.42 0 01-3.138 3.138 3.42 3.42 0 00-1.946.806 3.42 3.42 0 01-4.438 0 3.42 3.42 0 00-1.946-.806 3.42 3.42 0 01-3.138-3.138 3.42 3.42 0 00-.806-1.946 3.42 3.42 0 010-4.438 3.42 3.42 0 00.806-1.946 3.42 3.42 0 013.138-3.138z" />
          </svg>
          <h2 className="book-title text-xl">Recommended for You</h2>
        </div>
        <div className="text-center py-8">
          <div className="mb-6">
            <svg className="w-16 h-16 text-brand-primary mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z" />
            </svg>
          </div>
          <p className="text-goodreads-text mb-3 text-lg">
            {newUserMessage}
          </p>
          <p className="book-description mb-6">
            Start interacting with books to get personalized recommendations based on similar users' preferences.
          </p>
          <div className="space-y-3">
            <div className="flex items-center justify-center space-x-6">
              <div className="flex items-center text-green-600">
                <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 10h4.764a2 2 0 011.789 2.894l-3.5 7A2 2 0 0115.263 21h-4.017c-.163 0-.326-.02-.485-.06L7 20m7-10V5a2 2 0 00-2-2h-.095c-.5 0-.905.405-.905.905 0 .714-.211 1.412-.608 2.006L7 11v9m7-10h-2M7 20H5a2 2 0 01-2-2v-6a2 2 0 012-2h2.5" />
                </svg>
                <span>Like books you enjoy</span>
              </div>
              <div className="flex items-center text-red-600">
                <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 14H5.236a2 2 0 01-1.789-2.894l3.5-7A2 2 0 018.736 3h4.018c.163 0 .326.02.485.06L17 4m-7 10v5a2 2 0 002 2h.095c.5 0 .905-.405.905-.905 0-.714.211-1.412.608-2.006L17 13V4m-7 10h2m5-10h2a2 2 0 012 2v6a2 2 0 01-2 2h-2.5" />
                </svg>
                <span>Pass on books that don't interest you</span>
              </div>
            </div>
            <p className="text-goodreads-border text-sm">
              ðŸ’¡ Start with popular books below, then search for your favorite titles!
            </p>
          </div>
        </div>
      </div>
    );
  }

  if (error || recommendations.length === 0) {
    return (
      <div className="sidebar-section rounded-2xl mb-12">
        <div className="flex items-center mb-4">
          <svg className="w-6 h-6 text-brand-primary mr-3" fill="currentColor" viewBox="0 0 20 20">
            <path d="M9 12l2 2 4-4M7.835 4.697a3.42 3.42 0 001.946-.806 3.42 3.42 0 014.438 0 3.42 3.42 0 001.946.806 3.42 3.42 0 013.138 3.138 3.42 3.42 0 00.806 1.946 3.42 3.42 0 010 4.438 3.42 3.42 0 00-.806 1.946 3.42 3.42 0 01-3.138 3.138 3.42 3.42 0 00-1.946.806 3.42 3.42 0 01-4.438 0 3.42 3.42 0 00-1.946-.806 3.42 3.42 0 01-3.138-3.138 3.42 3.42 0 00-.806-1.946 3.42 3.42 0 010-4.438 3.42 3.42 0 00.806-1.946 3.42 3.42 0 013.138-3.138z" />
          </svg>
          <h2 className="book-title text-xl">Recommended for You</h2>
        </div>
        <div className="text-center py-8">
          <p className="book-description mb-2">
            {error || 'Like a few more books to get personalized recommendations!'}
          </p>
          <p className="text-goodreads-border text-sm">
            Based on similar users' preferences and collaborative filtering.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="goodreads-card-white rounded-2xl p-6 mb-12 shadow-md">
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center">
          <svg className="w-6 h-6 text-brand-primary mr-3" fill="currentColor" viewBox="0 0 20 20">
            <path d="M9 12l2 2 4-4M7.835 4.697a3.42 3.42 0 001.946-.806 3.42 3.42 0 014.438 0 3.42 3.42 0 001.946.806 3.42 3.42 0 013.138 3.138 3.42 3.42 0 00.806 1.946 3.42 3.42 0 010 4.438 3.42 3.42 0 00-.806 1.946 3.42 3.42 0 01-3.138 3.138 3.42 3.42 0 00-1.946.806 3.42 3.42 0 01-4.438 0 3.42 3.42 0 00-1.946-.806 3.42 3.42 0 01-3.138-3.138 3.42 3.42 0 00-.806-1.946 3.42 3.42 0 010-4.438 3.42 3.42 0 00.806-1.946 3.42 3.42 0 013.138-3.138z" />
          </svg>
          <h2 className="book-title text-xl">Recommended for You</h2>
          <span className="ml-3 text-sm text-brand-primary bg-goodreads-cream px-2 py-1 rounded-full">
            Based on similar users
          </span>
          {interactionCount > 0 && (
            <span className="ml-2 text-xs text-goodreads-border">
              Based on {interactionCount} interactions
            </span>
          )}
        </div>
        <button
          onClick={fetchRecommendations}
          className="text-goodreads-border hover:text-brand-hover transition-all duration-300 p-2 rounded-lg hover:bg-goodreads-cream"
          title="Refresh recommendations"
        >
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
          </svg>
        </button>
      </div>

      <div className="flex overflow-x-auto space-x-6 pb-4 scrollbar-hide scroll-smooth">
        {recommendations.map((book, index) => (
          <div key={index} className="recommendation-card rounded-xl group flex-none w-72 relative">
            {/* Wishlist Star Button - Top Right Corner */}
            <button
              onClick={() => handleWishlistToggle(book)}
              className={`absolute top-2 right-2 p-1 rounded-full transition-all duration-200 ${
                book.in_wishlist
                  ? 'bg-yellow-500/20 text-yellow-600 border border-yellow-500'
                  : 'bg-goodreads-border/20 text-goodreads-border border border-goodreads-border hover:bg-yellow-500/20 hover:text-yellow-600 hover:border-yellow-500'
              }`}
              title={book.in_wishlist ? 'Remove from wishlist' : 'Add to wishlist'}
            >
              <svg className="w-4 h-4" fill={book.in_wishlist ? "currentColor" : "none"} stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11.049 2.927c.3-.921 1.603-.921 1.902 0l1.519 4.674a1 1 0 00.95.69h4.915c.969 0 1.371 1.24.588 1.81l-3.976 2.888a1 1 0 00-.363 1.118l1.518 4.674c.3.922-.755 1.688-1.538 1.118l-3.976-2.888a1 1 0 00-1.176 0l-3.976 2.888c-.783.57-1.838-.197-1.538-1.118l1.518-4.674a1 1 0 00-.363-1.118l-3.976-2.888c-.784-.57-.38-1.81.588-1.81h4.914a1 1 0 00.951-.69l1.519-4.674z" />
              </svg>
            </button>

            <div className="mb-3">
              <h4 className="book-title text-base leading-tight mb-2 line-clamp-2 pr-8" title={book.name}>
                {book.name}
              </h4>
              <p className="author-name text-sm mb-3 line-clamp-1" title={book.author}>
                {book.author || 'Unknown Author'}
              </p>
              
            </div>

            {/* Action Buttons - Show on hover */}
            <div className="opacity-0 group-hover:opacity-100 transition-all duration-300 flex space-x-3 mt-4">
              <button
                onClick={() => handleBookInteraction(book, 'like')}
                className="flex-1 flex items-center justify-center px-3 py-2 rounded-lg bg-green-500/20 border border-green-500/30 text-green-600 hover:bg-green-500/30 transition-all duration-300 text-sm font-medium"
                title="Like this book"
              >
                <svg className="w-3 h-3 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 10h4.764a2 2 0 011.789 2.894l-3.5 7A2 2 0 0115.263 21h-4.017c-.163 0-.326-.02-.485-.06L7 20m7-10V5a2 2 0 00-2-2h-.095c-.5 0-.905.405-.905.905 0 .714-.211 1.412-.608 2.006L7 11v9m7-10h-2M7 20H5a2 2 0 01-2-2v-6a2 2 0 012-2h2.5" />
                </svg>
                Like
              </button>
              <button
                onClick={() => handleBookInteraction(book, 'dislike')}
                className="flex-1 flex items-center justify-center px-3 py-2 rounded-lg bg-red-500/20 border border-red-500/30 text-red-600 hover:bg-red-500/30 transition-all duration-300 text-sm font-medium"
                title="Dislike this book"
              >
                <svg className="w-3 h-3 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 14H5.236a2 2 0 01-1.789-2.894l3.5-7A2 2 0 018.736 3h4.018c.163 0 .326.02.485.06L17 4m-7 10v5a2 2 0 002 2h.095c.5 0 .905-.405.905-.905 0-.714.211-1.412.608-2.006L17 13V4m-7 10h2m5-10h2a2 2 0 012 2v6a2 2 0 01-2 2h-2.5" />
                </svg>
                Pass
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecommendedForYouSection;
