import React, { useState, useCallback } from 'react';
import axios from 'axios';
import debounce from 'lodash/debounce';
import StarRating from './StarRating';

const BookSearch = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [interactionLoading, setInteractionLoading] = useState({});
  const [ratingLoading, setRatingLoading] = useState({});


  // Debounced search function
  const debouncedSearch = useCallback(
    debounce(async (term) => {
      if (term.trim().length < 2) {
        setSearchResults([]);
        return;
      }

      setLoading(true);
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`http://localhost:5000/api/books/search?q=${encodeURIComponent(term)}`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        setSearchResults(response.data.books || []);
      } catch (error) {
        console.error('Search error:', error);
        setSearchResults([]);
      } finally {
        setLoading(false);
      }
    }, 300),
    []
  );

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);
    debouncedSearch(value);
  };

  const handleInteraction = async (book, action) => {
    const bookKey = `${book.name}-${book.author}`;
    setInteractionLoading(prev => ({ ...prev, [bookKey]: true }));

    try {
      const token = localStorage.getItem('token');
      const endpoint = action === 'remove' 
        ? 'http://localhost:5000/api/books/remove-interaction'
        : `http://localhost:5000/api/books/${action}`;

      const payload = {
        book_name: book.name,
        book_author: book.author || 'Unknown',
        book_genres: book.genres || ''
      };

      const response = await axios.post(endpoint, payload, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      // Update the book in search results
      setSearchResults(prevResults => 
        prevResults.map(b => 
          b.name === book.name && b.author === book.author
            ? { 
                ...b, 
                user_interaction: response.data.action,
                likes: response.data.likes,
                dislikes: response.data.dislikes
              }
            : b
        )
      );
    } catch (error) {
      console.error('Interaction error:', error);
    } finally {
      setInteractionLoading(prev => ({ ...prev, [bookKey]: false }));
    }
  };

  // Handle star rating
  const handleRating = async (book, rating) => {
    const bookKey = `${book.name}-${book.author}`;
    setRatingLoading(prev => ({ ...prev, [bookKey]: true }));

    try {
      const token = localStorage.getItem('token');
      const payload = {
        book_name: book.name,
        book_author: book.author || 'Unknown',
        book_genres: book.genres || '',
        rating: rating
      };

      const response = await axios.post('http://localhost:5000/api/books/rate', payload, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      // Update the book in search results
      setSearchResults(prevResults => 
        prevResults.map(b => 
          b.name === book.name && b.author === book.author
            ? { 
                ...b,
                user_rating: response.data.user_rating,
                average_rating: response.data.average_rating,
                total_ratings: response.data.total_ratings,
                user_interaction: response.data.action,
                likes: response.data.likes,
                dislikes: response.data.dislikes
              }
            : b
        )
      );
    } catch (error) {
      console.error('Rating error:', error);
    } finally {
      setRatingLoading(prev => ({ ...prev, [bookKey]: false }));
    }
  };

  return (
    <div className="w-full">
      {/* Search Bar */}
      <div className="mb-8">
        <div className="relative">
          <input
            type="text"
            value={searchTerm}
            onChange={handleSearchChange}
            placeholder="Search for books by title, author, or genre..."
            className="w-full px-6 py-4 pl-12 text-white bg-white/10 backdrop-blur-md rounded-2xl focus:outline-none focus:ring-2 focus:ring-blue-500 placeholder-gray-400 text-lg"
          />
          <svg className="absolute left-4 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
          </svg>
        </div>
      </div>

      {/* Loading State */}
      {loading && (
        <div className="flex justify-center items-center py-12">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
        </div>
      )}

      {/* No Results */}
      {!loading && searchTerm && searchResults.length === 0 && (
        <div className="text-center py-12">
          <svg className="mx-auto h-12 w-12 text-gray-400 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.172 16.172a4 4 0 015.656 0M9 10h.01M15 10h.01M12 20h.01M12 4h.01M20 12h.01M4 12h.01M16.364 7.636a4 4 0 00-5.656 0M7.636 16.364a4 4 0 005.656 0" />
          </svg>
          <p className="text-gray-400 text-lg">No books found for "{searchTerm}"</p>
          <p className="text-gray-500 text-sm mt-2">Try searching with different keywords</p>
        </div>
      )}

      {/* Search Results */}
      {searchResults.length > 0 && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {searchResults.map((book, index) => (
            <div key={`${book.name}-${book.author}-${index}`} className="glass-card rounded-2xl p-6 hover:shadow-xl transition-all duration-300">
              <div className="flex flex-col h-full">
                {/* Book Info */}
                <div className="mb-4">
                  <h3 className="text-xl font-bold text-white mb-2 line-clamp-2">{book.name}</h3>
                  <p className="text-gray-300 mb-1">by {book.author || 'Unknown Author'}</p>
                  <p className="text-sm text-gray-400 line-clamp-2">{book.genres || 'No genres specified'}</p>
                  
                  {/* Original Book Rating (if available) */}
                  {book.star_rating && (
                    <div className="flex items-center mt-2 text-sm text-gray-400">
                      <svg className="w-4 h-4 text-yellow-400 mr-1" fill="currentColor" viewBox="0 0 20 20">
                        <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                      </svg>
                      <span>Original: {book.star_rating.toFixed(1)} ({book.num_ratings || 0} ratings)</span>
                    </div>
                  )}
                </div>

                {/* Ratings Section */}
                <div className="space-y-4 mb-4">
                  {/* User Rating Section */}
                  <div className="pb-3 border-b border-gray-700">
                    <div className="text-xs text-gray-400 mb-1">Your Rating:</div>
                    <StarRating
                      rating={book.user_rating || 0}
                      onRatingChange={(rating) => handleRating(book, rating)}
                      readonly={ratingLoading[`${book.name}-${book.author}`]}
                      size="sm"
                      showLabel={false}
                    />
                    {book.user_rating > 0 && (
                      <span className="text-xs text-gray-400 ml-2">
                        {book.user_rating} star{book.user_rating !== 1 ? 's' : ''}
                      </span>
                    )}
                  </div>

                  {/* Community Rating Section */}
                  <div>
                    <div className="text-xs text-gray-400 mb-1">Community Rating:</div>
                    <StarRating
                      rating={0}
                      averageRating={book.average_rating || 0}
                      totalRatings={book.total_ratings || 0}
                      readonly={true}
                      size="sm"
                      showLabel={true}
                    />
                  </div>
                </div>

                {/* Action Buttons */}
                <div className="flex gap-3 mt-auto">
                  {/* Like Button */}
                  <button
                    onClick={() => handleInteraction(book, book.user_interaction === 'like' ? 'remove' : 'like')}
                    disabled={interactionLoading[`${book.name}-${book.author}`]}
                    className={`flex-1 flex items-center justify-center px-4 py-2 rounded-lg font-medium transition-all duration-200 ${
                      book.user_interaction === 'like'
                        ? 'bg-green-500/20 text-green-400 border border-green-500'
                        : 'bg-white/10 text-gray-300 border border-white/20 hover:bg-green-500/20 hover:text-green-400 hover:border-green-500'
                    } disabled:opacity-50`}
                  >
                    {interactionLoading[`${book.name}-${book.author}`] ? (
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-current"></div>
                    ) : (
                      <>
                        <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 10h4.764a2 2 0 011.789 2.894l-3.5 7A2 2 0 0115.263 21h-4.017c-.163 0-.326-.02-.485-.06L7 20m7-10V5a2 2 0 00-2-2h-.095c-.5 0-.905.405-.905.905 0 .714-.211 1.412-.608 2.006L7 11v9m7-10h-2M7 20H5a2 2 0 01-2-2v-6a2 2 0 012-2h2.5" />
                        </svg>
                        <span>{book.likes || 0}</span>
                      </>
                    )}
                  </button>

                  {/* Dislike Button */}
                  <button
                    onClick={() => handleInteraction(book, book.user_interaction === 'dislike' ? 'remove' : 'dislike')}
                    disabled={interactionLoading[`${book.name}-${book.author}`]}
                    className={`flex-1 flex items-center justify-center px-4 py-2 rounded-lg font-medium transition-all duration-200 ${
                      book.user_interaction === 'dislike'
                        ? 'bg-red-500/20 text-red-400 border border-red-500'
                        : 'bg-white/10 text-gray-300 border border-white/20 hover:bg-red-500/20 hover:text-red-400 hover:border-red-500'
                    } disabled:opacity-50`}
                  >
                    {interactionLoading[`${book.name}-${book.author}`] ? (
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-current"></div>
                    ) : (
                      <>
                        <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 14H5.236a2 2 0 01-1.789-2.894l3.5-7A2 2 0 018.736 3h4.018c.163 0 .326.02.485.06L17 4m-7 10v5a2 2 0 002 2h.095c.5 0 .905-.405.905-.905 0-.714.211-1.412.608-2.006L17 13V4m-7 10h2m5-10h2a2 2 0 012 2v6a2 2 0 01-2 2h-2.5" />
                        </svg>
                        <span>{book.dislikes || 0}</span>
                      </>
                    )}
                  </button>
                </div>

                {/* User Interaction Indicator */}
                {book.user_interaction && book.user_interaction !== 'rating' && (
                  <div className="flex items-center justify-center mt-3">
                    <span className={`text-xs px-3 py-1 rounded-full ${
                      book.user_interaction === 'like' 
                        ? 'bg-green-500/20 text-green-400' 
                        : 'bg-red-500/20 text-red-400'
                    }`}>
                      You {book.user_interaction}d this book
                    </span>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default BookSearch;
