import React, { useState, useEffect } from 'react';

const AnalyticsDashboard = () => {
  const [analytics, setAnalytics] = useState({
    totalBooks: 0,
    totalRatings: 0,
    averageRating: 0,
    favoriteGenres: [],
    readingStreak: 0,
    monthlyActivity: []
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate analytics data - in a real app, this would come from an API
    const fetchAnalytics = async () => {
      setLoading(true);
      try {
        // Simulate API delay
        await new Promise(resolve => setTimeout(resolve, 1000));
        
        // Mock data - replace with actual API call
        setAnalytics({
          totalBooks: 42,
          totalRatings: 28,
          averageRating: 4.2,
          favoriteGenres: [
            { genre: 'Science Fiction', count: 12, percentage: 35 },
            { genre: 'Fantasy', count: 8, percentage: 24 },
            { genre: 'Mystery', count: 6, percentage: 18 },
            { genre: 'Romance', count: 4, percentage: 12 },
            { genre: 'Biography', count: 4, percentage: 11 }
          ],
          readingStreak: 7,
          monthlyActivity: [
            { month: 'Jan', books: 3, ratings: 2 },
            { month: 'Feb', books: 5, ratings: 4 },
            { month: 'Mar', books: 4, ratings: 3 },
            { month: 'Apr', books: 6, ratings: 5 },
            { month: 'May', books: 8, ratings: 6 },
            { month: 'Jun', books: 7, ratings: 8 }
          ]
        });
      } catch (error) {
        console.error('Error fetching analytics:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchAnalytics();
  }, []);

  if (loading) {
    return (
      <div className="glass-card rounded-2xl p-6">
        <div className="flex items-center justify-center py-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-purple-400"></div>
          <span className="ml-3 text-gray-400">Loading analytics...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="glass-card rounded-2xl p-6">
      <div className="flex items-center mb-6">
        <svg className="w-6 h-6 text-purple-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
        </svg>
        <h3 className="text-xl font-semibold text-white">Your Analytics</h3>
      </div>

      {/* Key Metrics */}
      <div className="grid grid-cols-2 gap-4 mb-6">
        <div className="bg-white/5 rounded-lg p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Total Books</p>
              <p className="text-2xl font-bold text-white">{analytics.totalBooks}</p>
            </div>
            <div className="w-8 h-8 bg-blue-500/20 rounded-lg flex items-center justify-center">
              <svg className="w-4 h-4 text-blue-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.746 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
              </svg>
            </div>
          </div>
        </div>

        <div className="bg-white/5 rounded-lg p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Avg Rating</p>
              <p className="text-2xl font-bold text-white">{analytics.averageRating.toFixed(1)}</p>
            </div>
            <div className="w-8 h-8 bg-yellow-500/20 rounded-lg flex items-center justify-center">
              <svg className="w-4 h-4 text-yellow-400" fill="currentColor" viewBox="0 0 20 20">
                <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
              </svg>
            </div>
          </div>
        </div>
      </div>

      {/* Favorite Genres */}
      <div className="mb-6">
        <h4 className="text-lg font-semibold text-white mb-3">Favorite Genres</h4>
        <div className="space-y-3">
          {analytics.favoriteGenres.slice(0, 3).map((item, index) => (
            <div key={item.genre} className="flex items-center justify-between">
              <div className="flex items-center">
                <div className={`w-3 h-3 rounded-full mr-3 ${
                  index === 0 ? 'bg-purple-400' :
                  index === 1 ? 'bg-blue-400' :
                  'bg-green-400'
                }`}></div>
                <span className="text-gray-300 text-sm">{item.genre}</span>
              </div>
              <div className="flex items-center">
                <div className="w-16 bg-gray-700 rounded-full h-2 mr-2">
                  <div 
                    className={`h-2 rounded-full ${
                      index === 0 ? 'bg-purple-400' :
                      index === 1 ? 'bg-blue-400' :
                      'bg-green-400'
                    }`}
                    style={{ width: `${item.percentage}%` }}
                  ></div>
                </div>
                <span className="text-gray-400 text-xs">{item.count}</span>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Reading Activity Chart */}
      <div className="mb-6">
        <h4 className="text-lg font-semibold text-white mb-3">Reading Activity</h4>
        <div className="flex items-end justify-between h-24 bg-white/5 rounded-lg p-3">
          {analytics.monthlyActivity.map((month, index) => (
            <div key={month.month} className="flex flex-col items-center flex-1">
              <div 
                className="bg-purple-400 rounded-t w-4 mb-1 transition-all duration-500"
                style={{ 
                  height: `${(month.books / Math.max(...analytics.monthlyActivity.map(m => m.books))) * 60}px`,
                  minHeight: '4px'
                }}
              ></div>
              <span className="text-gray-400 text-xs">{month.month}</span>
            </div>
          ))}
        </div>
        <div className="flex justify-between text-xs text-gray-400 mt-2">
          <span>Books read per month</span>
          <span>Last 6 months</span>
        </div>
      </div>

      {/* Reading Streak */}
      <div className="bg-gradient-to-r from-orange-500/10 to-red-500/10 rounded-lg p-4 border-l-4 border-orange-500">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-orange-400 font-semibold">Reading Streak</p>
            <p className="text-white text-xl font-bold">{analytics.readingStreak} days</p>
            <p className="text-gray-400 text-sm">Keep it up!</p>
          </div>
          <div className="w-12 h-12 bg-orange-500/20 rounded-full flex items-center justify-center">
            <svg className="w-6 h-6 text-orange-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 18.657A8 8 0 016.343 7.343S7 9 9 10c0-2 .5-5 2.986-7C14 5 16.09 5.777 17.656 7.343A7.975 7.975 0 0120 13a7.975 7.975 0 01-2.343 5.657z" />
            </svg>
          </div>
        </div>
      </div>

      {/* Achievement Badges */}
      <div className="mt-6 pt-6 border-t border-gray-700">
        <h4 className="text-lg font-semibold text-white mb-3">Recent Achievements</h4>
        <div className="flex space-x-3">
          <div className="flex items-center bg-yellow-500/20 text-yellow-400 px-3 py-2 rounded-lg text-sm">
            <svg className="w-4 h-4 mr-2" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M6.267 3.455a3.066 3.066 0 001.745-.723 3.066 3.066 0 013.976 0 3.066 3.066 0 001.745.723 3.066 3.066 0 012.812 2.812c.051.643.304 1.254.723 1.745a3.066 3.066 0 010 3.976 3.066 3.066 0 00-.723 1.745 3.066 3.066 0 01-2.812 2.812 3.066 3.066 0 00-1.745.723 3.066 3.066 0 01-3.976 0 3.066 3.066 0 00-1.745-.723 3.066 3.066 0 01-2.812-2.812 3.066 3.066 0 00-.723-1.745 3.066 3.066 0 010-3.976 3.066 3.066 0 00.723-1.745 3.066 3.066 0 012.812-2.812zm7.44 5.252a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
            </svg>
            First Rating
          </div>
          <div className="flex items-center bg-purple-500/20 text-purple-400 px-3 py-2 rounded-lg text-sm">
            <svg className="w-4 h-4 mr-2" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M6.267 3.455a3.066 3.066 0 001.745-.723 3.066 3.066 0 013.976 0 3.066 3.066 0 001.745.723 3.066 3.066 0 012.812 2.812c.051.643.304 1.254.723 1.745a3.066 3.066 0 010 3.976 3.066 3.066 0 00-.723 1.745 3.066 3.066 0 01-2.812 2.812 3.066 3.066 0 00-1.745.723 3.066 3.066 0 01-3.976 0 3.066 3.066 0 00-1.745-.723 3.066 3.066 0 01-2.812-2.812 3.066 3.066 0 00-.723-1.745 3.066 3.066 0 010-3.976 3.066 3.066 0 00.723-1.745 3.066 3.066 0 012.812-2.812zm7.44 5.252a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
            </svg>
            10 Books
          </div>
        </div>
      </div>
    </div>
  );
};

export default AnalyticsDashboard;
