import React from 'react';
import NetflixStyleRecommendedSection from './NetflixStyleRecommendedSection';
import BecauseYouLikedSection from './BecauseYouLikedSection';
import BecauseYouLikedAlternativeSection from './BecauseYouLikedAlternativeSection';
import BasedOnYourLikesSection from './BasedOnYourLikesSection';
import BestFromAuthorSection from './BestFromAuthorSection';
import ContinueReadingSection from './ContinueReadingSection';
import PopularBooksSection from './PopularBooksSection';
import PersonalizedGenreDashboards from './PersonalizedGenreDashboards';

const HomePage = () => {
  return (
    <div className="animate-slideUp">
      {/* Netflix-style Hero Section - Minimal and Clean */}
      <section className="px-6 py-8 mb-8">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-semibold text-[#553B08] mb-2">
              Discover Your Next Favorite Book
            </h1>
            <p className="text-[#4A4A4A] text-base">
              Personalized recommendations powered by AI and community insights
            </p>
          </div>
          <div className="hidden md:block">
            <div className="w-16 h-16 bg-gradient-to-br from-[#553B08] to-[#75420E] rounded-full flex items-center justify-center">
              <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.746 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
              </svg>
            </div>
          </div>
        </div>
      </section>

      {/* Netflix-style Content Sections - No spacing between sections */}
      <div className="space-y-8">
        {/* 1. Recommended for You */}
        <NetflixStyleRecommendedSection />
        
        {/* 2. Because You Liked */}
        <BecauseYouLikedSection />
        
        {/* 2b. Books Such As These (Alternative Because You Liked) */}
        <BecauseYouLikedAlternativeSection />
        
        {/* 3. Based on Your Reading Preferences */}
        <BasedOnYourLikesSection />
        
        {/* 4. Best From The Author */}
        <BestFromAuthorSection />
        
        {/* 5. Continue Reading */}
        <ContinueReadingSection />
        
        {/* 6. Popular in Genres */}
        <PersonalizedGenreDashboards />
        
        {/* 7. Popular Books */}
        <PopularBooksSection />
      </div>
    </div>
  );
};

export default HomePage;
