import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PersonalizedGenreDashboards = () => {
  const [genreData, setGenreData] = useState({
    genre1: { genre: null, books: [], count: 0, like_count: 0 },
    genre2: { genre: null, books: [], count: 0, like_count: 0 }
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [hasEnoughData, setHasEnoughData] = useState(false);
  const [retryCount, setRetryCount] = useState(0);

  useEffect(() => {
    fetchPersonalizedGenreBooks();
  }, []);

  const fetchPersonalizedGenreBooks = async () => {
    // Create abort controller for timeout protection
    const controller = new AbortController();
    const timeoutId = setTimeout(() => {
      controller.abort();
    }, 15000); // 15 second timeout

    try {
      setLoading(true);
      setError(null);
      const token = localStorage.getItem('token');
      
      const response = await axios.get('http://localhost:5000/api/books/popular/by-user-genres?limit=5', {
        headers: { 'Authorization': `Bearer ${token}` },
        signal: controller.signal,
        timeout: 15000 // Additional axios timeout
      });
      
      const data = response.data;
      setGenreData({
        genre1: data.genre1,
        genre2: data.genre2
      });
      setHasEnoughData(data.has_enough_data);
      
      // Clear timeout on successful response
      clearTimeout(timeoutId);
      
    } catch (error) {
      clearTimeout(timeoutId);
      console.error('Error fetching personalized genre books:', error);
      
      if (error.name === 'AbortError') {
        setError('Request timed out. The server may be busy - please try again in a moment.');
      } else if (error.response?.status === 408) {
        setError('Server timeout - please try again.');
      } else if (error.response?.status >= 500) {
        setError('Server error - please try again later.');
      } else if (error.code === 'ECONNABORTED') {
        setError('Connection timeout - please check your internet connection.');
      } else {
        setError('Failed to load personalized recommendations. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleRetry = () => {
    if (retryCount < 3) {
      setRetryCount(prev => prev + 1);
      setError(null);
      fetchPersonalizedGenreBooks();
    }
  };

  const handleBookInteraction = async (book, action) => {
    try {
      const token = localStorage.getItem('token');
      const payload = {
        book_name: book.name,
        book_author: book.author || '',
        book_genres: book.genres || ''
      };

      let endpoint = '';
      if (action === 'like') {
        endpoint = 'http://localhost:5000/api/books/like';
      } else if (action === 'dislike') {
        endpoint = 'http://localhost:5000/api/books/dislike';
      }

      await axios.post(endpoint, payload, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      // Refresh the data after interaction
      fetchPersonalizedGenreBooks();

    } catch (error) {
      console.error('Interaction error:', error);
    }
  };

  const renderGenreDashboard = (genreInfo, title, bgColor, iconColor) => {
    if (!genreInfo.genre) {
      return null;
    }

    return (
      <div className={`glass-card rounded-2xl p-6 ${bgColor}`}>
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center">
            <svg className={`w-6 h-6 ${iconColor} mr-3`} fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M3 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm0 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm0 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm0 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1z" clipRule="evenodd" />
            </svg>
            <div>
              <h3 className="text-lg font-semibold text-white">{title}</h3>
              <p className="text-sm text-gray-300">
                Popular in <span className="font-medium text-white">{genreInfo.genre}</span>
              </p>
            </div>
          </div>
          <div className={`text-xs ${iconColor} bg-white/10 px-3 py-1 rounded-full`}>
            {genreInfo.like_count} likes in this genre
          </div>
        </div>

        <div className="grid gap-4">
          {genreInfo.books.map((book, index) => (
            <div key={index} className="recommendation-card bg-white/5 backdrop-blur-sm border border-white/10 rounded-xl p-4 hover:bg-white/10 hover:border-orange-400/30 transition-all duration-300 group">
              <div className="flex justify-between items-start mb-3">
                <div className="flex-1 min-w-0">
                  <h4 className="font-semibold text-white text-sm leading-snug mb-1 line-clamp-2" title={book.name}>
                    {book.name}
                  </h4>
                  <p className="text-xs text-gray-400 mb-2 line-clamp-1" title={book.author}>
                    {book.author || 'Unknown Author'}
                  </p>
                  
                  {/* Rating */}
                  {book.star_rating ? (
                    <div className="flex items-center text-xs text-yellow-400 mb-2">
                      <svg className="w-3 h-3 mr-1" fill="currentColor" viewBox="0 0 20 20">
                        <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                      </svg>
                      <span>{book.star_rating.toFixed(1)}</span>
                      <span className="text-gray-500 ml-1">({book.num_ratings})</span>
                    </div>
                  ) : (
                    <div className="text-xs text-gray-500 mb-2">No rating</div>
                  )}
                </div>

                {/* Popularity Score Badge */}
                <div className={`${iconColor} bg-white/10 text-xs px-2 py-1 rounded-full`}>
                  {book.popularity_score}
                </div>
              </div>

              {/* User Interaction Status */}
              {book.user_interaction && (
                <div className="text-xs text-green-400 mb-2 flex items-center">
                  <svg className="w-3 h-3 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                  </svg>
                  You {book.user_interaction}d this
                </div>
              )}

              {/* Action Buttons - Show on hover, hide if already interacted */}
              {!book.user_interaction && (
                <div className="opacity-0 group-hover:opacity-100 transition-opacity duration-200 flex space-x-2 mt-2">
                  <button
                    onClick={() => handleBookInteraction(book, 'like')}
                    className="flex-1 flex items-center justify-center px-2 py-1 rounded bg-green-500/20 border border-green-500/30 text-green-400 hover:bg-green-500/30 transition-all duration-200 text-xs"
                    title="Like this book"
                  >
                    <svg className="w-3 h-3 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 10h4.764a2 2 0 011.789 2.894l-3.5 7A2 2 0 0115.263 21h-4.017c-.163 0-.326-.02-.485-.06L7 20m7-10V5a2 2 0 00-2-2h-.095c-.5 0-.905.405-.905.905 0 .714-.211 1.412-.608 2.006L7 11v9m7-10h-2M7 20H5a2 2 0 01-2-2v-6a2 2 0 012-2h2.5" />
                    </svg>
                    Like
                  </button>
                  <button
                    onClick={() => handleBookInteraction(book, 'dislike')}
                    className="flex-1 flex items-center justify-center px-2 py-1 rounded bg-red-500/20 border border-red-500/30 text-red-400 hover:bg-red-500/30 transition-all duration-200 text-xs"
                    title="Dislike this book"
                  >
                    <svg className="w-3 h-3 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 14H5.236a2 2 0 01-1.789-2.894l3.5-7A2 2 0 018.736 3h4.018c.163 0 .326.02.485.06L17 4m-7 10v5a2 2 0 002 2h.095c.5 0 .905-.405.905-.905 0-.714.211-1.412.608-2.006L17 13V4m-7 10h2m5-10h2a2 2 0 012 2v6a2 2 0 01-2 2h-2.5" />
                    </svg>
                    Pass
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="mb-8">
        <div className="flex items-center justify-center py-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-orange-400"></div>
          <span className="ml-3 text-gray-400">Loading personalized dashboards...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="glass-card rounded-2xl p-6 mb-8">
        <div className="text-center py-8">
          <p className="text-gray-400 mb-2">{error}</p>
          <p className="text-gray-500 text-sm">Please try again later.</p>
        </div>
      </div>
    );
  }

  if (!hasEnoughData) {
    return (
      <div className="glass-card rounded-2xl p-6 mb-8">
        <div className="flex items-center mb-4">
          <svg className="w-6 h-6 text-orange-400 mr-3" fill="currentColor" viewBox="0 0 20 20">
            <path fillRule="evenodd" d="M3 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm0 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm0 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm0 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1z" clipRule="evenodd" />
          </svg>
          <h2 className="text-xl font-semibold text-white">Personalized Genre Dashboards</h2>
        </div>
        <div className="text-center py-8">
          <p className="text-gray-400 mb-2">
            Like books from at least 2 different genres to see your personalized dashboards
          </p>
          <p className="text-gray-500 text-sm">
            These dashboards will show popular books in your most liked genres!
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="mb-8">
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-white mb-2">Your Personalized Dashboards</h2>
        <p className="text-gray-400">Popular books in your most liked genres</p>
      </div>

      <div className="grid lg:grid-cols-2 gap-6">
        {renderGenreDashboard(
          genreData.genre1,
          "Dashboard 1",
          "bg-gradient-to-br from-purple-500/10 to-pink-500/10 border-purple-400/20",
          "text-purple-400"
        )}
        
        {renderGenreDashboard(
          genreData.genre2,
          "Dashboard 2", 
          "bg-gradient-to-br from-blue-500/10 to-cyan-500/10 border-blue-400/20",
          "text-blue-400"
        )}
      </div>
    </div>
  );
};

export default PersonalizedGenreDashboards;
