import React, { useState } from 'react';
import BookSearch from './components/BookSearch';
import PopularBooks from './components/PopularBooks';
import './App.css';

function App() {
  const [activeTab, setActiveTab] = useState('search');
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('token'));

  const handleLogin = () => {
    // This would be your actual login logic
    // For now, just set a demo token
    localStorage.setItem('token', 'demo-token');
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuthenticated(false);
  };

  if (!isAuthenticated) {
    return (
      <div className="login-container">
        <div className="login-card">
          <h1>üìö Book Recommendation App</h1>
          <p>Please log in to search books and see recommendations</p>
          <button onClick={handleLogin} className="login-button">
            Demo Login
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="app-header">
        <h1>üìö Book Recommendation System</h1>
        <nav className="nav-tabs">
          <button
            className={`nav-tab ${activeTab === 'search' ? 'active' : ''}`}
            onClick={() => setActiveTab('search')}
          >
            üîç Search Books
          </button>
          <button
            className={`nav-tab ${activeTab === 'popular' ? 'active' : ''}`}
            onClick={() => setActiveTab('popular')}
          >
            üî• Popular Books
          </button>
          <button onClick={handleLogout} className="logout-button">
            Logout
          </button>
        </nav>
      </header>

      <main className="app-main">
        {activeTab === 'search' && <BookSearch />}
        {activeTab === 'popular' && <PopularBooks />}
      </main>

      <footer className="app-footer">
        <p>
          ‚ö° Powered by optimized connection pooling & smart caching | 
          üöÄ No timeouts - searches until results found |
          üíæ Results cached forever for instant loading
        </p>
      </footer>
    </div>
  );
}

export default App;
