import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PopularBooksSection = () => {
  const [popularBooks, setPopularBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchPopularBooks();
  }, []);

  const fetchPopularBooks = async (forceRefresh = false) => {
    try {
      const isRefreshing = forceRefresh;
      if (isRefreshing) {
        setRefreshing(true);
      } else {
        setLoading(true);
      }
      
      const token = localStorage.getItem('token');
      let url = 'http://localhost:5000/api/books/popular?limit=50';
      
      if (forceRefresh) {
        // Use the refresh endpoint for force refresh
        url = 'http://localhost:5000/api/books/popular/refresh?limit=50';
        const response = await axios.post(url, {}, {
          headers: { 'Authorization': `Bearer ${token}` }
        });
        setPopularBooks(response.data.books || []);
      } else {
        // Regular fetch with optional refresh parameter
        const response = await axios.get(url, {
          headers: { 'Authorization': `Bearer ${token}` }
        });
        setPopularBooks(response.data.books || []);
      }
      
      setError(null);
    } catch (error) {
      console.error('Error fetching popular books:', error);
      setError('Failed to load popular books');
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  };

  const handleRefresh = () => {
    fetchPopularBooks(true); // Force refresh
  };

  const handleBookInteraction = async (book, action) => {
    try {
      const token = localStorage.getItem('token');
      const payload = {
        book_name: book.name,
        book_author: book.author || '',
        book_genres: book.genres || ''
      };

      let endpoint = '';
      if (action === 'like') {
        endpoint = 'http://localhost:5000/api/books/like';
      } else if (action === 'dislike') {
        endpoint = 'http://localhost:5000/api/books/dislike';
      }

      await axios.post(endpoint, payload, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      // Refresh popular books after interaction to update user interaction status
      fetchPopularBooks();

    } catch (error) {
      console.error('Interaction error:', error);
    }
  };

  const handleWishlistToggle = async (book) => {
    try {
      const token = localStorage.getItem('token');
      const payload = {
        book_name: book.name,
        book_author: book.author || '',
        book_genres: book.genres || ''
      };

      await axios.post('http://localhost:5000/api/wishlist/toggle', payload, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      // Refresh popular books to update wishlist status
      fetchPopularBooks();

    } catch (error) {
      console.error('Wishlist error:', error);
    }
  };

  if (loading) {
    return (
      <div className="sidebar-section rounded-2xl mb-8">
        <div className="flex items-center justify-center py-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-brand-primary"></div>
          <span className="ml-3 text-goodreads-text/60">Loading popular books...</span>
        </div>
      </div>
    );
  }

  if (error || popularBooks.length === 0) {
    return (
      <div className="sidebar-section rounded-2xl mb-8">
        <div className="flex items-center mb-4">
          <svg className="w-6 h-6 text-brand-primary mr-3" fill="currentColor" viewBox="0 0 20 20">
            <path fillRule="evenodd" d="M12.395 2.553a1 1 0 00-1.45-.385c-.345.23-.614.558-.822.88-.214.33-.403.713-.57 1.116-.334.804-.614 1.768-.84 2.734a31.365 31.365 0 00-.613 3.58 2.64 2.64 0 01-.945-1.067c-.328-.68-.398-1.534-.398-2.654A1 1 0 005.05 6.05 6.981 6.981 0 003 11a7 7 0 1011.95-4.95c-.592-.591-.98-.985-1.348-1.467-.363-.476-.724-1.063-1.207-2.03zM12.12 15.12A3 3 0 017 13s.879.5 2.5.5c0-1 .5-4 1.25-4.5.5 1 .786 1.293 1.371 1.879A2.99 2.99 0 0113 13a2.99 2.99 0 01-.879 2.121z" clipRule="evenodd" />
          </svg>
          <h2 className="book-title text-xl">Popular Books</h2>
        </div>
        <div className="text-center py-8">
          <p className="book-description mb-2">
            {error || 'No popular books available at the moment.'}
          </p>
          <p className="text-goodreads-border text-sm">
            Please try again later.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="goodreads-card-white rounded-2xl p-6 mb-8">
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center">
          <svg className="w-6 h-6 text-brand-primary mr-3" fill="currentColor" viewBox="0 0 20 20">
            <path fillRule="evenodd" d="M12.395 2.553a1 1 0 00-1.45-.385c-.345.23-.614.558-.822.88-.214.33-.403.713-.57 1.116-.334.804-.614 1.768-.84 2.734a31.365 31.365 0 00-.613 3.58 2.64 2.64 0 01-.945-1.067c-.328-.68-.398-1.534-.398-2.654A1 1 0 005.05 6.05 6.981 6.981 0 003 11a7 7 0 1011.95-4.95c-.592-.591-.98-.985-1.348-1.467-.363-.476-.724-1.063-1.207-2.03zM12.12 15.12A3 3 0 017 13s.879.5 2.5.5c0-1 .5-4 1.25-4.5.5 1 .786 1.293 1.371 1.879A2.99 2.99 0 0113 13a2.99 2.99 0 01-.879 2.121z" clipRule="evenodd" />
          </svg>
          <h2 className="book-title text-xl">Popular Books</h2>
          {refreshing && (
            <span className="ml-2 text-sm text-goodreads-text bg-goodreads-cream px-2 py-1 rounded-full flex items-center">
              <div className="animate-spin rounded-full h-3 w-3 border-b border-brand-primary mr-1"></div>
              Refreshing
            </span>
          )}
        </div>
        <div className="flex items-center space-x-2">
          <button
            onClick={handleRefresh}
            disabled={refreshing}
            className={`text-goodreads-border hover:text-brand-primary transition-colors duration-200 ${
              refreshing ? 'opacity-50 cursor-not-allowed' : ''
            }`}
            title="Force refresh (recalculate popularity)"
          >
            <svg
              className={`w-5 h-5 ${refreshing ? 'animate-spin' : ''}`}
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
            </svg>
          </button>
        </div>
      </div>

      <div className="flex overflow-x-auto space-x-4 pb-4 scrollbar-thin scrollbar-thumb-gray-600 scrollbar-track-gray-800">
        {popularBooks
          .filter(book => !book.user_interaction) // Filter out books user has already interacted with
          .reduce((unique, book) => {
            // More aggressive deduplication: check if book name already exists
            const isDuplicate = unique.some(existingBook =>
              existingBook.name.toLowerCase().trim() === book.name.toLowerCase().trim()
            );
            if (!isDuplicate) {
              unique.push(book);
            }
            return unique;
          }, [])
          .slice(0, 10) // Limit to exactly 10 unique books
          .map((book, index) => (
          <div key={index} className="recommendation-card rounded-xl group flex-none w-64 relative">
            {/* Wishlist Star Button - Top Right Corner */}
            <button
              onClick={() => handleWishlistToggle(book)}
              className={`absolute top-2 right-2 p-1 rounded-full transition-all duration-200 ${
                book.in_wishlist
                  ? 'bg-yellow-500/20 text-yellow-600 border border-yellow-500'
                  : 'bg-goodreads-border/20 text-goodreads-border border border-goodreads-border hover:bg-yellow-500/20 hover:text-yellow-600 hover:border-yellow-500'
              }`}
              title={book.in_wishlist ? 'Remove from wishlist' : 'Add to wishlist'}
            >
              <svg className="w-4 h-4" fill={book.in_wishlist ? "currentColor" : "none"} stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11.049 2.927c.3-.921 1.603-.921 1.902 0l1.519 4.674a1 1 0 00.95.69h4.915c.969 0 1.371 1.24.588 1.81l-3.976 2.888a1 1 0 00-.363 1.118l1.518 4.674c.3.922-.755 1.688-1.538 1.118l-3.976-2.888a1 1 0 00-1.176 0l-3.976 2.888c-.783.57-1.838-.197-1.538-1.118l1.518-4.674a1 1 0 00-.363-1.118l-3.976-2.888c-.784-.57-.38-1.81.588-1.81h4.914a1 1 0 00.951-.69l1.519-4.674z" />
              </svg>
            </button>

            <div className="mb-3">
              <h4 className="book-title text-sm leading-snug mb-1 line-clamp-2 pr-8" title={book.name}>
                {book.name}
              </h4>
              <p className="author-name text-xs mb-2 line-clamp-1" title={book.author}>
                {book.author || 'Unknown Author'}
              </p>
              

              {/* User Interaction Status */}
              {book.user_interaction && (
                <div className="text-xs text-green-600 mb-2 flex items-center">
                  <svg className="w-3 h-3 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                  </svg>
                  You {book.user_interaction}d this
                </div>
              )}
            </div>

            {/* Action Buttons - Show on hover, hide if already interacted */}
            {!book.user_interaction && (
              <div className="opacity-0 group-hover:opacity-100 transition-opacity duration-200 flex space-x-2">
                <button
                  onClick={() => handleBookInteraction(book, 'like')}
                  className="flex-1 flex items-center justify-center px-2 py-1 rounded bg-green-500/20 border border-green-500/30 text-green-400 hover:bg-green-500/30 transition-all duration-200 text-xs"
                  title="Like this book"
                >
                  <svg className="w-3 h-3 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 10h4.764a2 2 0 011.789 2.894l-3.5 7A2 2 0 0115.263 21h-4.017c-.163 0-.326-.02-.485-.06L7 20m7-10V5a2 2 0 00-2-2h-.095c-.5 0-.905.405-.905.905 0 .714-.211 1.412-.608 2.006L7 11v9m7-10h-2M7 20H5a2 2 0 01-2-2v-6a2 2 0 012-2h2.5" />
                  </svg>
                  Like
                </button>
                <button
                  onClick={() => handleBookInteraction(book, 'dislike')}
                  className="flex-1 flex items-center justify-center px-2 py-1 rounded bg-red-500/20 border border-red-500/30 text-red-400 hover:bg-red-500/30 transition-all duration-200 text-xs"
                  title="Dislike this book"
                >
                  <svg className="w-3 h-3 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 14H5.236a2 2 0 01-1.789-2.894l3.5-7A2 2 0 018.736 3h4.018c.163 0 .326.02.485.06L17 4m-7 10v5a2 2 0 002 2h.095c.5 0 .905-.405.905-.905 0-.714.211-1.412.608-2.006L17 13V4m-7 10h2m5-10h2a2 2 0 012 2v6a2 2 0 01-2 2h-2.5" />
                  </svg>
                  Pass
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default PopularBooksSection;
