import React, { useState } from 'react';

const StarRating = ({ 
  rating = 0, 
  onRatingChange = null, 
  readonly = false, 
  size = 'md',
  showLabel = true,
  totalRatings = null,
  averageRating = null 
}) => {
  const [hoverRating, setHoverRating] = useState(0);
  
  const sizes = {
    sm: 'w-4 h-4',
    md: 'w-5 h-5',
    lg: 'w-6 h-6'
  };
  
  const handleClick = (newRating) => {
    if (!readonly && onRatingChange) {
      onRatingChange(newRating);
    }
  };
  
  const handleMouseEnter = (newRating) => {
    if (!readonly) {
      setHoverRating(newRating);
    }
  };
  
  const handleMouseLeave = () => {
    setHoverRating(0);
  };
  
  const renderStar = (starNumber) => {
    // For interactive rating (user's rating), use whole numbers
    const displayRating = readonly && averageRating !== null ? averageRating : (hoverRating || rating);
    
    // Calculate fill percentage for this star
    let fillPercentage = 0;
    if (displayRating >= starNumber) {
      fillPercentage = 100;
    } else if (displayRating > starNumber - 1) {
      // Partial fill for fractional ratings (only for readonly mode)
      fillPercentage = readonly ? (displayRating - (starNumber - 1)) * 100 : 0;
    }
    
    const isHovered = hoverRating >= starNumber;
    const uniqueId = `star-${starNumber}-${Math.random().toString(36).substr(2, 9)}`;
    
    return (
      <button
        key={starNumber}
        type="button"
        onClick={() => handleClick(starNumber)}
        onMouseEnter={() => handleMouseEnter(starNumber)}
        onMouseLeave={handleMouseLeave}
        className={`${readonly ? 'cursor-default' : 'cursor-pointer'} focus:outline-none transition-all duration-200 ${
          !readonly ? 'hover:scale-110' : ''
        }`}
        disabled={readonly}
      >
        <svg
          className={`${sizes[size]} transition-colors duration-200`}
          viewBox="0 0 24 24"
          xmlns="http://www.w3.org/2000/svg"
        >
          <defs>
            <linearGradient id={uniqueId}>
              <stop offset={`${fillPercentage}%`} stopColor={isHovered && !readonly ? '#fde047' : '#facc15'} />
              <stop offset={`${fillPercentage}%`} stopColor="#4b5563" />
            </linearGradient>
          </defs>
          <path
            fill={fillPercentage > 0 ? `url(#${uniqueId})` : 'none'}
            stroke={fillPercentage > 0 ? 'none' : '#4b5563'}
            strokeWidth="1.5"
            strokeLinecap="round"
            strokeLinejoin="round"
            d="M11.48 3.499a.562.562 0 011.04 0l2.125 5.111a.563.563 0 00.475.345l5.518.442c.499.04.701.663.321.988l-4.204 3.602a.563.563 0 00-.182.557l1.285 5.385a.562.562 0 01-.84.61l-4.725-2.885a.563.563 0 00-.586 0L6.982 20.54a.562.562 0 01-.84-.61l1.285-5.386a.562.562 0 00-.182-.557l-4.204-3.602a.563.563 0 01.321-.988l5.518-.442a.563.563 0 00.475-.345L11.48 3.5z"
            filter={fillPercentage > 0 ? 'drop-shadow(0 1px 2px rgba(0, 0, 0, 0.1))' : 'none'}
          />
        </svg>
      </button>
    );
  };
  
  // Display the actual rating value for reference
  const displayValue = averageRating !== null ? averageRating : rating;
  
  return (
    <div className="flex flex-col gap-1">
      <div className="flex items-center gap-2">
        <div className="flex gap-0.5">
          {[1, 2, 3, 4, 5].map(renderStar)}
        </div>
        {showLabel && rating > 0 && !averageRating && (
          <span className="text-sm text-gray-400">
            {rating} star{rating !== 1 ? 's' : ''}
          </span>
        )}
        {showLabel && averageRating !== null && (
          <div className="flex items-center gap-2 text-sm">
            <span className="text-yellow-400 font-medium">{averageRating.toFixed(1)}</span>
            {totalRatings !== null && (
              <span className="text-gray-400">({totalRatings} rating{totalRatings !== 1 ? 's' : ''})</span>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default StarRating;
