import React, { useState, useRef } from 'react';
import './BookSearch.css';

const BookSearch = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [hasSearched, setHasSearched] = useState(false);
  const [error, setError] = useState('');
  
  // AbortController ref to cancel previous searches
  const abortControllerRef = useRef(null);

  const handleSearch = async () => {
    if (!searchQuery.trim()) {
      setError('Please enter a search term');
      return;
    }

    // Cancel previous search if it exists
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
    }

    // Create new AbortController for this search
    abortControllerRef.current = new AbortController();
    
    setIsSearching(true);
    setError('');
    setHasSearched(true);

    try {
      const token = localStorage.getItem('token');
      
      const response = await fetch(
        `http://localhost:5000/api/books/search?q=${encodeURIComponent(searchQuery)}&limit=50`,
        {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
          signal: abortControllerRef.current.signal
        }
      );

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setSearchResults(data.books || []);
      
    } catch (error) {
      // Don't show error for aborted requests
      if (error.name !== 'AbortError') {
        console.error('Search error:', error);
        setError('Failed to search books. Please try again.');
        setSearchResults([]);
      }
    } finally {
      setIsSearching(false);
      abortControllerRef.current = null;
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  const handleLikeBook = async (book) => {
    try {
      const token = localStorage.getItem('token');
      
      const response = await fetch('http://localhost:5000/api/books/like', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          book_name: book.name,
          book_author: book.author,
          book_genres: book.genres
        })
      });

      if (response.ok) {
        // Update the book in search results
        setSearchResults(prevResults =>
          prevResults.map(b =>
            b.name === book.name 
              ? { ...b, user_interaction: 'like', likes: (b.likes || 0) + 1 }
              : b
          )
        );
      }
    } catch (error) {
      console.error('Error liking book:', error);
    }
  };

  const handleDislikeBook = async (book) => {
    try {
      const token = localStorage.getItem('token');
      
      const response = await fetch('http://localhost:5000/api/books/dislike', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          book_name: book.name,
          book_author: book.author,
          book_genres: book.genres
        })
      });

      if (response.ok) {
        // Update the book in search results
        setSearchResults(prevResults =>
          prevResults.map(b =>
            b.name === book.name 
              ? { ...b, user_interaction: 'dislike', dislikes: (b.dislikes || 0) + 1 }
              : b
          )
        );
      }
    } catch (error) {
      console.error('Error disliking book:', error);
    }
  };

  return (
    <div className="book-search">
      <div className="search-header">
        <h2>Search Books</h2>
        <div className="search-container">
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Enter book name to search..."
            className="search-input"
            disabled={isSearching}
          />
          <button
            onClick={handleSearch}
            disabled={!searchQuery.trim() || isSearching}
            className="search-button"
          >
            {isSearching ? 'Searching...' : 'Search'}
          </button>
        </div>
        
        {error && <div className="error-message">{error}</div>}
        
        {isSearching && (
          <div className="loading-message">
            <div className="spinner"></div>
            Searching for books...
          </div>
        )}
      </div>

      {hasSearched && !isSearching && (
        <div className="search-results">
          <h3>Search Results ({searchResults.length} books found)</h3>
          
          {searchResults.length === 0 ? (
            <div className="no-results">
              <p>No books found matching "{searchQuery}"</p>
              <p>Try different keywords or check spelling</p>
            </div>
          ) : (
            <div className="books-grid">
              {searchResults.map((book, index) => (
                <div key={index} className="book-card">
                  <div className="book-info">
                    <h4 className="book-title">{book.name}</h4>
                    <p className="book-author">by {book.author}</p>
                    <p className="book-genres">{book.genres}</p>
                    
                    <div className="book-stats">
                      <div className="rating">
                        ⭐ {book.star_rating || 'N/A'} 
                        ({book.num_ratings || 0} ratings)
                      </div>
                      
                      <div className="interactions">
                        👍 {book.likes || 0} | 👎 {book.dislikes || 0}
                      </div>
                      
                      {book.average_rating && (
                        <div className="community-rating">
                          Community: ⭐ {book.average_rating} 
                          ({book.total_ratings} ratings)
                        </div>
                      )}
                    </div>
                  </div>
                  
                  <div className="book-actions">
                    <button
                      onClick={() => handleLikeBook(book)}
                      className={`action-button like-button ${book.user_interaction === 'like' ? 'active' : ''}`}
                      disabled={book.user_interaction === 'like'}
                    >
                      👍 {book.user_interaction === 'like' ? 'Liked' : 'Like'}
                    </button>
                    
                    <button
                      onClick={() => handleDislikeBook(book)}
                      className={`action-button dislike-button ${book.user_interaction === 'dislike' ? 'active' : ''}`}
                      disabled={book.user_interaction === 'dislike'}
                    >
                      👎 {book.user_interaction === 'dislike' ? 'Disliked' : 'Dislike'}
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default BookSearch;
