import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserPreferences = () => {
  const [likedGenres, setLikedGenres] = useState([]);
  const [dislikedGenres, setDislikedGenres] = useState([]);
  const [likedBooks, setLikedBooks] = useState([]);
  const [dislikedBooks, setDislikedBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('genres');

  useEffect(() => {
    fetchUserPreferences();
  }, []);

  const fetchUserPreferences = async () => {
    try {
      const token = localStorage.getItem('token');
      const headers = {
        'Authorization': `Bearer ${token}`
      };

      // Fetch all data in parallel
      const [likedGenresRes, dislikedGenresRes, likedBooksRes, dislikedBooksRes] = await Promise.all([
        axios.get('http://localhost:5000/api/books/liked-genres', { headers }),
        axios.get('http://localhost:5000/api/books/disliked-genres', { headers }),
        axios.get('http://localhost:5000/api/books/user-interactions?action=like', { headers }),
        axios.get('http://localhost:5000/api/books/user-interactions?action=dislike', { headers })
      ]);

      setLikedGenres(likedGenresRes.data.genres || []);
      setDislikedGenres(dislikedGenresRes.data.genres || []);
      setLikedBooks(likedBooksRes.data.interactions || []);
      setDislikedBooks(dislikedBooksRes.data.interactions || []);
    } catch (error) {
      console.error('Error fetching user preferences:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  if (loading) {
    return (
      <div className="w-full">
        <div className="glass-card rounded-2xl p-6">
          <div className="flex items-center justify-center py-12">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-gray-400"></div>
            <span className="ml-3 text-gray-400">Loading your preferences...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="w-full">
      {/* Header */}
      <div className="glass-card rounded-2xl p-6 mb-8">
        <div className="flex items-center mb-4">
          <svg className="w-6 h-6 text-gray-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
          </svg>
          <h2 className="text-xl font-semibold text-white">Your Reading Preferences</h2>
        </div>
        
        {/* Tab Navigation */}
        <div className="flex space-x-4">
          <button
            onClick={() => setActiveTab('genres')}
            className={`px-4 py-2 rounded-lg font-medium transition-all duration-200 ${
              activeTab === 'genres'
                ? 'bg-blue-500/20 text-blue-400 border border-blue-500'
                : 'bg-white/5 text-gray-400 border border-white/20 hover:bg-white/10'
            }`}
          >
            Genre Preferences
          </button>
          <button
            onClick={() => setActiveTab('books')}
            className={`px-4 py-2 rounded-lg font-medium transition-all duration-200 ${
              activeTab === 'books'
                ? 'bg-blue-500/20 text-blue-400 border border-blue-500'
                : 'bg-white/5 text-gray-400 border border-white/20 hover:bg-white/10'
            }`}
          >
            Book History
          </button>
        </div>
      </div>

      {/* Content */}
      {activeTab === 'genres' && (
        <div className="grid md:grid-cols-2 gap-8">
          {/* Liked Genres */}
          <div className="glass-card rounded-2xl p-6">
            <div className="flex items-center mb-6">
              <svg className="w-5 h-5 text-green-400 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 10h4.764a2 2 0 011.789 2.894l-3.5 7A2 2 0 0115.263 21h-4.017c-.163 0-.326-.02-.485-.06L7 20m7-10V5a2 2 0 00-2-2h-.095c-.5 0-.905.405-.905.905 0 .714-.211 1.412-.608 2.006L7 11v9m7-10h-2M7 20H5a2 2 0 01-2-2v-6a2 2 0 012-2h2.5" />
              </svg>
              <h3 className="text-lg font-semibold text-white">Favorite Genres</h3>
              <span className="ml-auto text-sm text-gray-400">({likedGenres.length})</span>
            </div>

            {likedGenres.length === 0 ? (
              <div className="text-center py-8">
                <svg className="w-12 h-12 text-gray-400 mx-auto mb-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 7h.01M7 3h5c.512 0 1.024.195 1.414.586l7 7a2 2 0 010 2.828l-7 7a2 2 0 01-2.828 0l-7-7A1.994 1.994 0 013 12V7a4 4 0 014-4z" />
                </svg>
                <p className="text-gray-400">No favorite genres yet</p>
                <p className="text-gray-500 text-sm mt-1">Like some books to see your preferences!</p>
              </div>
            ) : (
              <div className="space-y-3">
                {likedGenres.map((genre, index) => (
                  <div key={index} className="flex items-center justify-between bg-green-500/10 border border-green-500/20 rounded-lg p-3">
                    <span className="text-green-300 font-medium">{genre.genre}</span>
                    <div className="flex items-center space-x-2">
                      <span className="text-sm text-green-400">{genre.count} book{genre.count !== 1 ? 's' : ''}</span>
                      <div className="w-16 bg-green-500/20 rounded-full h-2">
                        <div 
                          className="bg-green-500 h-2 rounded-full transition-all duration-300"
                          style={{ width: `${Math.min((genre.count / Math.max(...likedGenres.map(g => g.count))) * 100, 100)}%` }}
                        ></div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Disliked Genres */}
          <div className="glass-card rounded-2xl p-6">
            <div className="flex items-center mb-6">
              <svg className="w-5 h-5 text-red-400 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 14H5.236a2 2 0 01-1.789-2.894l3.5-7A2 2 0 018.736 3h4.018c.163 0 .326.02.485.06L17 4m-7 10v5a2 2 0 002 2h.095c.5 0 .905-.405.905-.905 0-.714.211-1.412.608-2.006L17 13V4m-7 10h2m5-10h2a2 2 0 012 2v6a2 2 0 01-2 2h-2.5" />
              </svg>
              <h3 className="text-lg font-semibold text-white">Disliked Genres</h3>
              <span className="ml-auto text-sm text-gray-400">({dislikedGenres.length})</span>
            </div>

            {dislikedGenres.length === 0 ? (
              <div className="text-center py-8">
                <svg className="w-12 h-12 text-gray-400 mx-auto mb-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M18.364 18.364A9 9 0 005.636 5.636m12.728 12.728L5.636 5.636m12.728 12.728L18.364 5.636M5.636 18.364l12.728-12.728" />
                </svg>
                <p className="text-gray-400">No disliked genres</p>
                <p className="text-gray-500 text-sm mt-1">Dislike books to track preferences</p>
              </div>
            ) : (
              <div className="space-y-3">
                {dislikedGenres.map((genre, index) => (
                  <div key={index} className="flex items-center justify-between bg-red-500/10 border border-red-500/20 rounded-lg p-3">
                    <span className="text-red-300 font-medium">{genre.genre}</span>
                    <div className="flex items-center space-x-2">
                      <span className="text-sm text-red-400">{genre.count} book{genre.count !== 1 ? 's' : ''}</span>
                      <div className="w-16 bg-red-500/20 rounded-full h-2">
                        <div 
                          className="bg-red-500 h-2 rounded-full transition-all duration-300"
                          style={{ width: `${Math.min((genre.count / Math.max(...dislikedGenres.map(g => g.count))) * 100, 100)}%` }}
                        ></div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      )}

      {activeTab === 'books' && (
        <div className="grid md:grid-cols-2 gap-8">
          {/* Liked Books */}
          <div className="glass-card rounded-2xl p-6">
            <div className="flex items-center mb-6">
              <svg className="w-5 h-5 text-green-400 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 10h4.764a2 2 0 011.789 2.894l-3.5 7A2 2 0 0115.263 21h-4.017c-.163 0-.326-.02-.485-.06L7 20m7-10V5a2 2 0 00-2-2h-.095c-.5 0-.905.405-.905.905 0 .714-.211 1.412-.608 2.006L7 11v9m7-10h-2M7 20H5a2 2 0 01-2-2v-6a2 2 0 012-2h2.5" />
              </svg>
              <h3 className="text-lg font-semibold text-white">Liked Books</h3>
              <span className="ml-auto text-sm text-gray-400">({likedBooks.length})</span>
            </div>

            {likedBooks.length === 0 ? (
              <div className="text-center py-8">
                <svg className="w-12 h-12 text-gray-400 mx-auto mb-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.746 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
                </svg>
                <p className="text-gray-400">No liked books yet</p>
                <p className="text-gray-500 text-sm mt-1">Start liking books to see them here!</p>
              </div>
            ) : (
              <div className="space-y-3 max-h-96 overflow-y-auto">
                {likedBooks.map((book, index) => (
                  <div key={index} className="bg-green-500/10 border border-green-500/20 rounded-lg p-3">
                    <h4 className="text-green-300 font-medium mb-1">{book.book_name}</h4>
                    <p className="text-sm text-gray-400 mb-1">by {book.book_author || 'Unknown Author'}</p>
                    <div className="flex items-center justify-between">
                      <span className="text-xs text-green-400">
                        {book.book_genres && book.book_genres.length > 0 ? book.book_genres.join(', ') : 'No genres'}
                      </span>
                      <span className="text-xs text-gray-500">{formatDate(book.timestamp)}</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Disliked Books */}
          <div className="glass-card rounded-2xl p-6">
            <div className="flex items-center mb-6">
              <svg className="w-5 h-5 text-red-400 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 14H5.236a2 2 0 01-1.789-2.894l3.5-7A2 2 0 018.736 3h4.018c.163 0 .326.02.485.06L17 4m-7 10v5a2 2 0 002 2h.095c.5 0 .905-.405.905-.905 0-.714.211-1.412.608-2.006L17 13V4m-7 10h2m5-10h2a2 2 0 012 2v6a2 2 0 01-2 2h-2.5" />
              </svg>
              <h3 className="text-lg font-semibold text-white">Disliked Books</h3>
              <span className="ml-auto text-sm text-gray-400">({dislikedBooks.length})</span>
            </div>

            {dislikedBooks.length === 0 ? (
              <div className="text-center py-8">
                <svg className="w-12 h-12 text-gray-400 mx-auto mb-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M18.364 18.364A9 9 0 005.636 5.636m12.728 12.728L5.636 5.636m12.728 12.728L18.364 5.636M5.636 18.364l12.728-12.728" />
                </svg>
                <p className="text-gray-400">No disliked books</p>
                <p className="text-gray-500 text-sm mt-1">Disliked books will appear here</p>
              </div>
            ) : (
              <div className="space-y-3 max-h-96 overflow-y-auto">
                {dislikedBooks.map((book, index) => (
                  <div key={index} className="bg-red-500/10 border border-red-500/20 rounded-lg p-3">
                    <h4 className="text-red-300 font-medium mb-1">{book.book_name}</h4>
                    <p className="text-sm text-gray-400 mb-1">by {book.book_author || 'Unknown Author'}</p>
                    <div className="flex items-center justify-between">
                      <span className="text-xs text-red-400">
                        {book.book_genres && book.book_genres.length > 0 ? book.book_genres.join(', ') : 'No genres'}
                      </span>
                      <span className="text-xs text-gray-500">{formatDate(book.timestamp)}</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default UserPreferences;
