import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import StarRating from './StarRating';

const ContinueReadingSection = () => {
  const { token } = useAuth();
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [refreshing, setRefreshing] = useState(false);
  const [processingTime, setProcessingTime] = useState(0);
  const [fromCache, setFromCache] = useState(false);

  useEffect(() => {
    fetchRecommendations();
  }, []);

  const fetchRecommendations = async (forceRefresh = true) => {
    try {
      setLoading(true);
      setError(null);
      
      // AUTO-REFRESH: Always use refresh endpoint on initial load for fresh data
      const endpoint = forceRefresh
        ? 'http://localhost:5000/api/continue-reading/refresh'
        : 'http://localhost:5000/api/continue-reading';
      
      const method = forceRefresh ? 'POST' : 'GET';
      
      console.log(`🔄 Continue Reading: ${forceRefresh ? 'Fetching FRESH data' : 'Using cached data'}`);
      
      const response = await fetch(endpoint, {
        method: method,
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      const data = await response.json();
      
      if (response.ok) {
        setRecommendations(data.recommendations || []);
        setProcessingTime(data.processing_time || 0);
        setFromCache(data.from_cache || false);
        setError(null);
        
        // Enhanced logging for empty recommendations
        if (data.recommendations?.length === 0) {
          console.warn("No continue reading recommendations available");
          console.log("Response data:", data);
        } else {
          console.log(`✅ Loaded ${data.recommendations.length} continue reading recommendations (${forceRefresh ? 'FRESH' : 'cached'})`);
        }
      } else {
        setError(data.message || 'Failed to load recommendations');
        setRecommendations([]);
        console.error("Failed to load recommendations:", data);
      }
    } catch (err) {
      setError('Network error occurred');
      setRecommendations([]);
      console.error('Continue reading fetch error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleRefresh = async () => {
    setRefreshing(true);
    await fetchRecommendations(true); // Force refresh
    setRefreshing(false);
  };

  const handleBookInteraction = async (bookName, action) => {
    try {
      const endpoint = action === 'like' ? '/api/books/like' : '/api/books/dislike';
      
      const response = await fetch(`http://localhost:5000${endpoint}`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          book_name: bookName,
          book_author: '',
          book_genres: ''
        })
      });

      if (response.ok) {
        // Refresh recommendations after interaction
        fetchRecommendations();
      }
    } catch (err) {
      console.error('Interaction error:', err);
    }
  };

  if (loading) {
    return (
      <div className="glass-card rounded-2xl p-6 mb-8 animate-slideUp">
        <div className="flex items-center justify-between mb-6">
          <div>
            <h2 className="text-2xl font-bold text-gray-100 flex items-center">
              <svg className="w-7 h-7 text-green-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.746 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253z" />
              </svg>
              Continue Reading
            </h2>
            <p className="text-gray-400 text-sm mt-1">Next books in series you've enjoyed</p>
          </div>
        </div>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {[1, 2, 3].map(i => (
            <div key={i} className="glass-card rounded-xl p-4 animate-pulse">
              <div className="h-4 bg-gray-600 rounded mb-2"></div>
              <div className="h-3 bg-gray-700 rounded mb-1"></div>
              <div className="h-3 bg-gray-700 rounded w-3/4 mb-3"></div>
              <div className="h-16 bg-gray-700 rounded mb-3"></div>
              <div className="flex justify-between">
                <div className="h-6 bg-gray-600 rounded w-20"></div>
                <div className="h-6 bg-gray-600 rounded w-16"></div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  if (error && recommendations.length === 0) {
    return (
      <div className="glass-card rounded-2xl p-6 mb-8 animate-slideUp">
        <div className="flex items-center justify-between mb-6">
          <div>
            <h2 className="text-2xl font-bold text-gray-100 flex items-center">
              <svg className="w-7 h-7 text-green-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.746 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253z" />
              </svg>
              Continue Reading
            </h2>
            <p className="text-gray-400 text-sm mt-1">Next books in series you've enjoyed</p>
          </div>
          <button
            onClick={handleRefresh}
            disabled={refreshing}
            className="bg-green-500/20 hover:bg-green-500/30 backdrop-blur-sm border border-green-500/50 px-4 py-2 rounded-lg text-green-400 font-medium transition-all duration-300 disabled:opacity-50"
          >
            {refreshing ? '🔄' : '↻'} Refresh
          </button>
        </div>
        <div className="text-center py-8">
          <svg className="w-16 h-16 text-gray-500 mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.746 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253z" />
          </svg>
          <h3 className="text-xl font-medium text-gray-300 mb-2">No Series Found</h3>
          <p className="text-gray-400 mb-4">Like some books to discover series recommendations!</p>
          <p className="text-sm text-red-400">{error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="glass-card rounded-2xl p-6 mb-8 animate-slideUp">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold text-gray-100 flex items-center">
            <svg className="w-7 h-7 text-green-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.746 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253z" />
            </svg>
            Continue Reading
            {recommendations.length > 0 && (
              <span className="ml-2 text-sm text-green-400 bg-green-400/20 px-2 py-1 rounded-full">
                {recommendations.length} Series
              </span>
            )}
          </h2>
          <p className="text-gray-400 text-sm mt-1">Next books in series you've enjoyed</p>
        </div>
        <div className="flex items-center space-x-3">
          <button
            onClick={handleRefresh}
            disabled={refreshing}
            className="bg-green-500/20 hover:bg-green-500/30 backdrop-blur-sm border border-green-500/50 px-4 py-2 rounded-lg text-green-400 font-medium transition-all duration-300 disabled:opacity-50"
          >
            {refreshing ? '🔄' : '↻'} Refresh
          </button>
        </div>
      </div>

      {recommendations.length === 0 ? (
        <div className="text-center py-8">
          <svg className="w-16 h-16 text-gray-500 mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.746 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253z" />
          </svg>
          <h3 className="text-xl font-medium text-gray-300 mb-2">No Series Found</h3>
          <p className="text-gray-400">Like some books from series to see continue reading recommendations!</p>
        </div>
      ) : (
        <div className="flex overflow-x-auto space-x-6 pb-4 scrollbar-thin scrollbar-thumb-gray-600 scrollbar-track-gray-800">
          {recommendations.map((rec, index) => (
            <ContinueReadingCard
              key={index}
              recommendation={rec}
              onInteraction={handleBookInteraction}
            />
          ))}
        </div>
      )}
    </div>
  );
};

const ContinueReadingCard = ({ recommendation, onInteraction }) => {
  const { next_book, attribution, series_name, confidence_score, verification_status } = recommendation;
  const [showFullDescription, setShowFullDescription] = useState(false);

  const description = next_book.description || 'No description available.';
  const shouldTruncate = description.length > 150;
  const displayDescription = shouldTruncate && !showFullDescription 
    ? description.slice(0, 150) + '...' 
    : description;

  const getVerificationBadge = () => {
    if (verification_status === 'verified') {
      return (
        <span className="inline-flex items-center px-2 py-1 rounded text-xs bg-green-500/20 text-green-400 border border-green-500/30">
          ✓ Verified
        </span>
      );
    }
    return null;
  };

  return (
    <div className="glass-card rounded-xl p-6 hover:bg-white/10 transition-all duration-300 transform hover:scale-105 flex-none w-80">
      <div className="mb-4">
        <h3 className="text-lg font-semibold text-gray-100 mb-2">{next_book.title}</h3>
        <p className="text-gray-300 text-sm mb-2">by {next_book.author}</p>
        <p className="text-green-400 text-xs font-medium mb-2">{attribution}</p>
        
        <div className="flex items-center gap-2 mb-3">
          <span className="bg-purple-500/20 text-purple-400 px-2 py-1 rounded text-xs">
            {series_name}
          </span>
          {getVerificationBadge()}
        </div>
      </div>

      <div className="mb-4">
        <p className="text-gray-400 text-sm leading-relaxed">
          {displayDescription}
        </p>
        {shouldTruncate && (
          <button
            onClick={() => setShowFullDescription(!showFullDescription)}
            className="text-blue-400 hover:text-blue-300 text-xs mt-1 font-medium"
          >
            {showFullDescription ? 'Show less' : 'Read more'}
          </button>
        )}
      </div>

      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <button
            onClick={() => onInteraction(next_book.title, 'like')}
            className="bg-green-500/20 hover:bg-green-500/30 text-green-400 px-3 py-1 rounded text-sm transition-all duration-200"
          >
            👍 Like
          </button>
          <button
            onClick={() => onInteraction(next_book.title, 'dislike')}
            className="bg-red-500/20 hover:bg-red-500/30 text-red-400 px-3 py-1 rounded text-sm transition-all duration-200"
          >
            👎 Pass
          </button>
        </div>
        
      </div>
    </div>
  );
};

export default ContinueReadingSection;
