import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import Header from './Header';
import HomePage from './HomePage';
import SearchResultsPage from './SearchResultsPage';
import UserPreferences from './UserPreferences';
import AnalyticsDashboard from './AnalyticsDashboard';
import Wishlist from './Wishlist';

const Dashboard = () => {
  const { user } = useAuth();
  const [currentTime, setCurrentTime] = useState(new Date());
  const [currentPage, setCurrentPage] = useState('home');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  const handleSearchSubmit = (term) => {
    setSearchTerm(term);
    setCurrentPage('search');
  };

  const handleNavigate = (page) => {
    setCurrentPage(page);
    if (page !== 'search') {
      setSearchTerm('');
    }
  };

  const formatDate = (date) => {
    return new Intl.DateTimeFormat('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    }).format(date);
  };

  const renderCurrentPage = () => {
    switch (currentPage) {
      case 'home':
        return <HomePage />;
      case 'search':
        return <SearchResultsPage searchTerm={searchTerm} onNavigate={handleNavigate} />;
      case 'preferences':
        return <UserPreferences />;
      case 'profile':
        return (
          <div className="space-y-8 animate-slideUp">
            {/* Analytics Dashboard */}
            <AnalyticsDashboard />
            
            {/* Profile Information Grid */}
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
            {/* User Info Card */}
            <div className="glass-card rounded-2xl p-6">
              <div className="flex items-center mb-4">
                <svg className="w-6 h-6 text-gray-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                </svg>
                <h2 className="text-xl font-medium text-gray-100">Profile Information</h2>
              </div>
              <div className="space-y-3">
                <div>
                  <label className="text-sm text-gray-400">Username</label>
                  <p className="text-gray-100 font-medium">{user?.username}</p>
                </div>
                <div>
                  <label className="text-sm text-gray-400">Email</label>
                  <p className="text-gray-100 font-medium">{user?.email}</p>
                </div>
                <div>
                  <label className="text-sm text-gray-400">User ID</label>
                  <p className="text-gray-300 text-sm font-mono">{user?.id}</p>
                </div>
              </div>
            </div>

            {/* Current Time Card */}
            <div className="glass-card rounded-2xl p-6">
              <div className="flex items-center mb-4">
                <svg className="w-6 h-6 text-gray-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                <h2 className="text-xl font-medium text-gray-100">Current Time</h2>
              </div>
              <div className="text-center">
                <p className="text-2xl font-bold text-gray-100">
                  {currentTime.toLocaleTimeString()}
                </p>
                <p className="text-gray-400 mt-2">
                  {formatDate(currentTime)}
                </p>
              </div>
            </div>

            {/* Session Status Card */}
            <div className="glass-card rounded-2xl p-6 md:col-span-2 lg:col-span-1">
              <div className="flex items-center mb-4">
                <svg className="w-6 h-6 text-green-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                <h2 className="text-xl font-medium text-gray-100">Session Status</h2>
              </div>
              <div className="space-y-3">
                <div className="flex items-center">
