import React, { useState, useEffect } from 'react';
import axios from 'axios';
import StarRating from './StarRating';

const BookSearch = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [hasSearched, setHasSearched] = useState(false);
  const [searchTimeout, setSearchTimeout] = useState(null);
  const [interactionLoading, setInteractionLoading] = useState({});
  const [ratingLoading, setRatingLoading] = useState({});

  // Debounced search function
  const performSearch = async (query) => {
    if (!query || query.length < 2) {
      setBooks([]);
      setHasSearched(false);
      return;
    }

    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:5000/api/books/search?q=${encodeURIComponent(query)}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setBooks(response.data.books || []);
      setHasSearched(true);
    } catch (error) {
      console.error('Search error:', error);
      setBooks([]);
      setHasSearched(true);
    } finally {
      setLoading(false);
    }
  };

  // Handle search input change with debouncing
  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    // Clear existing timeout
    if (searchTimeout) {
      clearTimeout(searchTimeout);
    }

    // Set new timeout for debounced search
    const newTimeout = setTimeout(() => {
      performSearch(value);
    }, 300);

    setSearchTimeout(newTimeout);
  };

  // Clear search
  const clearSearch = () => {
    setSearchTerm('');
    setBooks([]);
    setHasSearched(false);
    if (searchTimeout) {
      clearTimeout(searchTimeout);
    }
  };

  // Handle star rating
  const handleRating = async (book, rating) => {
    const bookKey = `${book.name}-${book.author}`;
    setRatingLoading(prev => ({ ...prev, [bookKey]: true }));

    try {
      const token = localStorage.getItem('token');
      const payload = {
        book_name: book.name,
        book_author: book.author || '',
        book_genres: book.genres || '',
        rating: rating
      };

      const response = await axios.post('http://localhost:5000/api/books/rate', payload, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      // Update the book in the local state
      setBooks(prevBooks => 
        prevBooks.map(b => {
          if (b.name === book.name && b.author === book.author) {
            return {
              ...b,
              user_rating: response.data.user_rating,
              average_rating: response.data.average_rating,
              total_ratings: response.data.total_ratings,
              user_interaction: response.data.action,
              likes: response.data.likes,
              dislikes: response.data.dislikes
            };
          }
          return b;
        })
      );

    } catch (error) {
      console.error('Rating error:', error);
    } finally {
      setRatingLoading(prev => ({ ...prev, [bookKey]: false }));
    }
  };

  // Handle like/dislike actions
  const handleBookInteraction = async (book, action) => {
    const bookKey = `${book.name}-${book.author}`;
    setInteractionLoading(prev => ({ ...prev, [bookKey]: true }));

    try {
      const token = localStorage.getItem('token');
      const payload = {
        book_name: book.name,
        book_author: book.author || '',
        book_genres: book.genres || ''
      };

      let endpoint = '';
      if (action === 'like') {
        endpoint = 'http://localhost:5000/api/books/like';
      } else if (action === 'dislike') {
        endpoint = 'http://localhost:5000/api/books/dislike';
      } else if (action === 'remove') {
        endpoint = 'http://localhost:5000/api/books/remove-interaction';
      }

      const response = await axios.post(endpoint, payload, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      // Update the book in the local state
      setBooks(prevBooks => 
        prevBooks.map(b => {
          if (b.name === book.name && b.author === book.author) {
            return {
              ...b,
              user_interaction: response.data.action,
              likes: response.data.likes,
              dislikes: response.data.dislikes
            };
          }
          return b;
        })
      );

    } catch (error) {
      console.error('Interaction error:', error);
    } finally {
      setInteractionLoading(prev => ({ ...prev, [bookKey]: false }));
    }
  };

  // Get button style based on interaction state
  const getButtonStyle = (book, buttonType) => {
    const bookKey = `${book.name}-${book.author}`;
    const isLoading = interactionLoading[bookKey];
    const userInteraction = book.user_interaction;

    if (isLoading) {
      return "opacity-50 cursor-not-allowed";
    }

    if (buttonType === 'like') {
      return userInteraction === 'like' 
        ? "bg-green-500/20 border-green-500 text-green-400 hover:bg-green-500/30"
        : "bg-white/5 border-white/20 text-gray-400 hover:bg-green-500/20 hover:border-green-500 hover:text-green-400";
    } else {
      return userInteraction === 'dislike'
        ? "bg-red-500/20 border-red-500 text-red-400 hover:bg-red-500/30"
        : "bg-white/5 border-white/20 text-gray-400 hover:bg-red-500/20 hover:border-red-500 hover:text-red-400";
    }
  };

  // Handle button click
  const handleButtonClick = (book, buttonType) => {
    const userInteraction = book.user_interaction;
    
    if (buttonType === 'like') {
      if (userInteraction === 'like') {
        handleBookInteraction(book, 'remove');
      } else {
        handleBookInteraction(book, 'like');
      }
    } else {
      if (userInteraction === 'dislike') {
        handleBookInteraction(book, 'remove');
      } else {
        handleBookInteraction(book, 'dislike');
      }
    }
  };

  // Cleanup timeout on unmount
  useEffect(() => {
    return () => {
      if (searchTimeout) {
        clearTimeout(searchTimeout);
      }
    };
  }, [searchTimeout]);

  return (
    <div className="w-full">
      {/* Search Bar */}
      <div className="glass-card rounded-2xl p-6 mb-8">
        <div className="flex items-center mb-4">
          <svg className="w-6 h-6 text-gray-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.746 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
          </svg>
          <h2 className="text-xl font-semibold text-white">Search Books</h2>
        </div>
        
        <div className="relative">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <svg className="h-5 w-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
            </svg>
          </div>
          <input
            type="text"
            value={searchTerm}
            onChange={handleSearchChange}
            className="glass-input w-full pl-10 pr-12 py-3 rounded-lg text-white placeholder-gray-400 focus:outline-none"
            placeholder="Search for books by name..."
          />
          {searchTerm && (
            <button
              onClick={clearSearch}
              className="absolute inset-y-0 right-0 pr-3 flex items-center text-gray-400 hover:text-white transition-colors duration-200"
            >
              <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          )}
        </div>
        
        {loading && (
          <div className="mt-4 flex items-center justify-center">
            <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-gray-400"></div>
            <span className="ml-2 text-gray-400">Searching...</span>
          </div>
        )}
      </div>

      {/* Search Results */}
      {hasSearched && (
        <div className="glass-card rounded-2xl p-6">
          <div className="flex items-center justify-between mb-6">
            <h3 className="text-lg font-semibold text-white">
              Search Results
            </h3>
            <span className="text-sm text-gray-400">
              {books.length} {books.length === 1 ? 'book' : 'books'} found
            </span>
          </div>

          {books.length === 0 ? (
            <div className="text-center py-12">
              <svg className="w-16 h-16 text-gray-400 mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.746 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
              </svg>
              <p className="text-gray-400 text-lg">No books found</p>
              <p className="text-gray-500 text-sm mt-2">Try searching with a different term</p>
            </div>
          ) : (
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
              {books.map((book, index) => (
                <div
                  key={index}
                  className="bg-white/5 backdrop-blur-sm border border-white/10 rounded-xl p-4 hover:bg-white/10 hover:border-white/20 transition-all duration-300"
                >
                  <div className="mb-4">
                    <h4 className="font-semibold text-white text-lg leading-snug mb-2">
                      {book.name}
                    </h4>
                    <div className="flex items-center text-sm text-gray-300 mb-2">
                      <svg className="w-4 h-4 mr-2 text-gray-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                      </svg>
                      <span>{book.author || 'Unknown Author'}</span>
                    </div>
                    <div className="flex items-center text-sm text-gray-400 mb-3">
                      <svg className="w-4 h-4 mr-2 text-purple-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 7h.01M7 3h5c.512 0 1.024.195 1.414.586l7 7a2 2 0 010 2.828l-7 7a2 2 0 01-2.828 0l-7-7A1.994 1.994 0 013 12V7a4 4 0 014-4z" />
                      </svg>
                      <span>{book.genres || 'No genre specified'}</span>
                    </div>
                    
                    {/* User Rating Section */}
                    <div className="mb-3">
                      <div className="text-xs text-gray-400 mb-1">Your Rating:</div>
                      <StarRating 
                        rating={book.user_rating || 0}
                        onRatingChange={(rating) => handleRating(book, rating)}
                        readonly={ratingLoading[`${book.name}-${book.author}`]}
                        size="sm"
                        showLabel={false}
                      />
                    </div>
                    
                    {/* Community Rating Section */}
                    <div className="mb-3">
                      <div className="text-xs text-gray-400 mb-1">Community Rating:</div>
                      <StarRating 
                        rating={book.average_rating || book.star_rating || 0}
                        averageRating={book.average_rating || book.star_rating}
                        totalRatings={book.total_ratings || book.num_ratings || 0}
                        readonly={true}
                        size="sm"
                      />
                    </div>
                  </div>

                  {/* Like/Dislike Buttons */}
                  <div className="flex items-center justify-between">
                    <div className="flex space-x-2">
                      {/* Like Button */}
                      <button
                        onClick={() => handleButtonClick(book, 'like')}
                        disabled={interactionLoading[`${book.name}-${book.author}`]}
                        className={`flex items-center px-3 py-2 rounded-lg border transition-all duration-200 ${getButtonStyle(book, 'like')}`}
                      >
                        <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 10h4.764a2 2 0 011.789 2.894l-3.5 7A2 2 0 0115.263 21h-4.017c-.163 0-.326-.02-.485-.06L7 20m7-10V5a2 2 0 00-2-2h-.095c-.5 0-.905.405-.905.905 0 .714-.211 1.412-.608 2.006L7 11v9m7-10h-2M7 20H5a2 2 0 01-2-2v-6a2 2 0 012-2h2.5" />
                        </svg>
                        <span className="text-sm font-medium">{book.likes || 0}</span>
                      </button>

                      {/* Dislike Button */}
                      <button
                        onClick={() => handleButtonClick(book, 'dislike')}
                        disabled={interactionLoading[`${book.name}-${book.author}`]}
                        className={`flex items-center px-3 py-2 rounded-lg border transition-all duration-200 ${getButtonStyle(book, 'dislike')}`}
                      >
                        <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 14H5.236a2 2 0 01-1.789-2.894l3.5-7A2 2 0 018.736 3h4.018c.163 0 .326.02.485.06L17 4m-7 10v5a2 2 0 002 2h.095c.5 0 .905-.405.905-.905 0-.714.211-1.412.608-2.006L17 13V4m-7 10h2m5-10h2a2 2 0 012 2v6a2 2 0 01-2 2h-2.5" />
                        </svg>
                        <span className="text-sm font-medium">{book.dislikes || 0}</span>
                      </button>
                    </div>

                    {/* User Interaction Indicator */}
                    {book.user_interaction && book.user_interaction !== 'rating' && (
                      <div className="flex items-center">
                        <span className={`text-xs font-medium px-2 py-1 rounded-full ${
                          book.user_interaction === 'like' 
                            ? 'bg-green-500/20 text-green-400' 
                            : 'bg-red-500/20 text-red-400'
                        }`}>
                          {book.user_interaction === 'like' ? 'Liked' : 'Disliked'}
                        </span>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default BookSearch;
